}

.icon-payment-visa:before {
  content: $icon-payment-visa;
}

.icon-payment-mastercard:before {
  content: $icon-payment-mastercard;
}

.icon-payment-american-express:before {
  content: $icon-payment-american-express;
}

.icon-payment-paypal:before {
  content: $icon-payment-paypal;
}

