/* scss-combine:general/typography */
// General

body {
  font-family: $body-font;
  font-size: $body-font-size;
  line-height: 1.666;
  color: $body-color;
}

// Links

a {
  color: $general-link-color;
  transition: color 0.2s $ease-out-quad;

  &:hover {
    @include hover($general-link-color);
  }
}

// Headers

h1,
h2,
h3,
h4,
h5,
h6 {
  @extend %heading-text-treatment;
  line-height: 1.25;
}

h1 { font-size: 36px; }

h2 { font-size: 28px; }

h3 { font-size: 20px; }

h4 { font-size: 18px; }

h5 { font-size: 14px; }

h6 { font-size: 14px; }

.page-title {
  @extend %heading-text-treatment;
  margin: 0;
  font-size: 26px;

  @include breakpoint(l-up) {
    @include grid-columns(6 of 12, row);
  }

}

.sub-header {
  @extend %heading-text-treatment;
  font-size: 18px;
}

.section-title {
  @extend %heading-text-treatment;
  font-size: 20px;
  color: $section-title-color;
  text-align: $section-title-align;
}

// Rich Text Editor spacing adjustments
.rte {
  @extend %clearfix;
  font-size: $body-font-size;
  text-align: left;

  > *:first-child {
    margin-top: 0;
  }

  > *:last-child {
    margin-bottom: 0;
  }

  ul {
    list-style: disc;
  }

  ul,
  ol,
  dl {
    margin-left: $gutter;
  }

  blockquote {
    padding-left: $gutter;
    margin-left: 0;
    border-left: 2px solid $accent-color;
  }

  img {
    height: auto;
  }

  .tabs {
    position: relative;
    z-index: 10;
    display: block;
    width: 100%;
    height: 44px;
    padding: 0;
    margin: 0;
    font-size: 0;
    white-space: nowrap;
  }

  .tab-title {
    display: inline-block;
    height: 45px;
    padding: 0 20px;
    margin: 0;
    font-size: 14px;
    font-weight: 300;
    line-height: 45px;
    letter-spacing: 0.05em;
    color: $heading-color;
    cursor: pointer;
    border: 1px solid transparent;
    border-bottom: 0;

    &.active {
      height: 44px;
      background: transparent;
      border-color: $border-color;
    }
  }

  .tabs-content {
    position: relative;
    top: -1px;
    z-index: 5;
    display: block;
    width: 100%;
    padding: 20px 0 0;
    margin: 0 0 30px;
    overflow: hidden;
    border-top: 1px solid $border-color;
  }

  .tab-content-panel {
    display: none;
    margin: 0;

    > *:first-child {
      margin-top: 0;
    }

    > *:last-child {
      margin-bottom: 0;
    }

    &.active { display: block; }
  }
}

/* scss-combine:general/general */
// Basic elements
hr {
  background: $border-color;
}

cite {
  color: $meta-color;
}

// Tables

table {
  color: $body-color;
  border-color: $border-color;
}

th {
  @extend %heading-text-treatment;
  color: $accent-color;
  background: $accent-color-light;
}

td,
th {
  border-top-color: $accent-color-light;
}

td {
  border-left-color: $accent-color-light;
}

.loading-overlay {
  @include page-overlay(map-get($zindex, overlay));
}

@keyframes spinner1 {
  0% { left: 0; }
  50% { left: 16px; }
  100% { left: 0; }
}

@keyframes spinner2 {
  0% {
    left: 16px;
    z-index: 1;
  }

  49% { z-index: 1; }

  50% {
    left: 0;
    z-index: 10;
  }

  100% {
    left: 16px;
    z-index: 10;
  }
}

.page-spinner {
  // scss-lint:disable VendorPrefix
  position: absolute;
  top: 50%;
  right: 50%;
  width: 32px;
  height: 16px;
  margin-top: -8px;
  margin-left: -16px;
  transform: translateX(100%);
  transition: transform 0.4s $ease-out-quad;
  -webkit-backface-visibility: hidden;

  &.visible {
  }

  .quick-shop .add-to-cart & {
    @include opacity(1);
    left: auto;
    margin-left: 0;
    visibility: visible;
  }
}

.page-spinner:before,
.page-spinner:after {
  position: absolute;
  width: 16px;
  height: 16px;
  border-radius: 8px;
  content: "";
}

.page-spinner:before {
  left: 0;
  z-index: 5;
  background: mix($body-background-color, $accent-color, 60%);
  animation: spinner1 1s linear infinite;
}

.page-spinner:after {
  left: 16px;
  background: mix($body-background-color, $accent-color, 40%);
  animation: spinner2 1s linear infinite;
}

/* scss-combine:general/messages */

.alert {
  position: relative;
  padding: 15px;
  margin-bottom: 1em;
  font-size: 13px;
  border: 1px solid $border-color;
  border-radius: 3px;
  transition: opacity 0.3s ease;

  &:last-child {
    margin-bottom: 0;
  }

  &.hidden { display: none; }

  .alert-dismiss {
    position: absolute;
    top: 4px;
    right: 6px;
    font-size: 16px;
    line-height: 16px;
    color: inherit;
    cursor: pointer;
    opacity: 0.8;

    &:hover { opacity: 1; }
  }

  &.alert-success,
  &.alert-error,
  &.alert-info {
    // scss-lint:disable VariableForProperty
    color: lighten($neutral-color, 10%);
    background-color: rgba($neutral-color, 0.05);
    border-color: rgba($neutral-color, 0.1);

    .dismiss { color: lighten($neutral-color, 20%); }
  }

  // make buttons small & tidy
  // currently only used in the 'cart add' success message
  .button {
    padding: 2px 10px 3px;
    font-weight: normal;
    color: $button-primary-text-color;
    background: $button-primary-background-color;
    border-color: $button-primary-border-color;

    &:hover {
      @include hover($button-primary-text-color);
      @include hover($button-primary-background-color, background);
      @include hover($button-primary-border-color, border-color);
    }
  }

  .actions {
    @extend %clearfix;
    @include row(100, 4);
    margin-top: 1em;

    a {
      @include fluid-width(48, 100, 4);
      display: block;
      float: left;
    }
  }
}
 //alerts
/* scss-combine:general/layout */
body {
  color: $body-color;
  background-color: $body-background-color;

  &.modal-locked,
  &.scroll-locked {
    overflow: hidden;
  }
}

.main-header {
  position: absolute;
  top: 0;
  z-index: map-get($zindex, header);
  width: 100%;
  min-height: $header-height-mobile;
  transition: top 1s $ease-out-quad;

  @include breakpoint(l-up) {
    .alternate-header & {
      height: $header-height-mobile;
      max-height: $header-height-mobile;
    }
  }

}

.main-content {
  // scss-lint:disable DeclarationOrder
  position: relative;
  top: 0;
  z-index: map-get($zindex, content);
  margin-top: $header-height-mobile;

  .has-carousel & {
    margin-top: 0;
  }

  @include breakpoint(l-up) {
    margin-top: $header-height-desktop;

    .has-carousel & {
      margin-top: 0;
    }
  }

  @include breakpoint(print) {
    margin-top: 0;
  }
}

.page-header {
  @extend %page-header;
}

.main-footer {
  z-index: map-get($zindex, footer);
  padding-bottom: $gutter * 2;
}

.layout-module {
  @extend %wrapper;
  @extend %clearfix;
  position: relative;
}

/* scss-combine:general/button */
// Buttons

// DEFAULT button is white with colour border
// SECONDARY button is colour with white text

.button {
  @extend %button-base;
}

.button-disabled,
.button:disabled {

  &,
  &:hover {
    color: $button-disabled-color;
    cursor: default;
    background: $button-disabled-background-color;
    border-color: $button-disabled-background-color;
  }
}

.button-small {
  font-family: $body-font;
  font-size: 12px;
  letter-spacing: 0;
  text-transform: uppercase;
}

.button-wide-medium {
  display: inline-block;
  width: 100%;
  max-width: 240px;
}

.button-wide {
  display: block;
  width: 100%;
}

.button-primary {
  @extend %button-primary;
}

.button-secondary {
  @extend %button-secondary;
}

.button-invisible-thin,
.button-invisible {
  color: $button-tertiary-text-color;
  background-color: $button-tertiary-background-color;
  border-width: 0;

  &:hover {
    @include hover($button-tertiary-background-color, background-color)
  }
}

.button-invisible-thin {
  font-weight: normal;
}

// add to cart ajax progress
.button-progress {
  //transition: opacity 0.2s;

  span:before {
    display: inline-block;
    width: 0;
    height: 1em;
    vertical-align: bottom;
    background: url("../images/spinner.svg");
    background-position: left center;
    background-repeat: no-repeat;
    background-size: 1em 1em; // 22/14
    content: "";
    opacity: 0;
    transition: all 0.4s ease;
  }

  &.progress {
    @include opacity(0.5);
    cursor: default;
    background: desaturate($button-primary-background-color, 100%);
    border-color: desaturate($button-primary-background-color, 100%);

    span:before {
      width: 2.14em; // 30/14
      opacity: 1;
    }
  }
}

/* scss-combine:general/form */
.form-row {
  @include grid-row(nest);
}

.form-field {
  margin-bottom: $form-gutter;
  font-size: $body-font-size;
  letter-spacing: 0.05em;
  color: $form-input-color;

  .form-row.row-of-2 & {
    @include grid-columns(1 of 2);
  }

  .form-row.row-of-1 & {
    @include grid-columns(1 of 1);
  }
}

.form-input {
  @extend %border-radius-treatment;
  width: 100%;
  max-width: 280px;
  min-width: 240px;
  padding: $form-element-padding;
  font-size: 14px;
  color: $form-input-color;
  background: $form-input-background;
  border-color: $form-input-border;
  transition: border-color 0.3s $ease-out-quad;

  .full-width-field & {
    max-width: 100%;
  }

  &::placeholder {
    @include hover($form-input-color);
  }

  &:focus {
    outline: 0;
  }

  &.form-error {
    color: $error-color;
    border: 1px solid $error-color;

    &::placeholder {
      @include hover($error-color);
    }
  }
}

.form-label-text,
.form-field-title {
  display: block;
  margin: 0 0 $form-gutter / 3;
  font-size: 14px;
  letter-spacing: 0.05em;
  color: $form-input-color;

  .required-text {
    // Additional styles in place in case these should be shown later
    color: $required-color;
  }
}

//-- Old style Checkboxes --//
//-- TODO: Remove all old occurances of these style, update them to match new standard --//
.input-checkbox,
.input-radio,
.input-rectangle {
  width: auto;
  max-width: 0;
  min-width: 0;
  background: none;
  border: 0;
  appearance: none;

  + .form-input-mask {
    // scss-lint:disable PlaceholderInExtend
    @extend .icon-unchecked;
    position: relative;
    top: 1px;
    display: inline-block;
    width: 10px;
    height: 10px;
    margin-right: $gutter / 3;
    cursor: pointer;
  }

  &:checked + .form-input-mask {
    // scss-lint:disable PlaceholderInExtend
    @extend .icon-checked;
  }

  ~ .form-label-text {
    margin: 0;
  }
}

//-- New style checkboxes --//
.form-radio,
.form-checkbox {
  @extend %checkbox-hide;

  + .form-label-text {
    margin-bottom: 0;
    vertical-align: middle;
    cursor: pointer;

    &:before {
      // scss-lint:disable PlaceholderInExtend
      @extend .icon-unchecked;
      position: relative;
      top: 1px;
      display: inline-block;
      width: 10px;
      height: 10px;
      margin-right: $gutter / 3;
      cursor: pointer;
      content: "";
    }

    &:hover {
      color: brightness-adjust($form-input-color);
    }
  }

  &:checked + .form-label-text {

    &:before {
      // scss-lint:disable PlaceholderInExtend
      @extend .icon-checked;
    }
  }
}

.form-rectangles {

  .form-label {
    position: relative;
    display: inline-block;
    margin-bottom: 5px;
  }

  .input-rectangle {
    position: absolute;
    width: 100%;

    ~ .form-label-text {
      @extend %border-radius-treatment;
      padding: 7px 12px;
      border: 1px solid $form-input-border;
    }

    &:checked ~ .form-label-text {
      color: $accent-color;
      border-color: $accent-color;
    }
  }
}

// Forms & buttons

.form-error-message {
  color: $error-color;
  background: transparent;
}

// scss-lint:disable QualifyingElement
input[type="password"] {
  .lt-ie9 & {
    font-family: Arial, sans-serif;
  }
}

.form-selected-text {
  @extend %show-for-sr;
}

.form-select-wrapper,
.select-wrapper {
  position: relative;
  display: block;
  max-width: 280px;
  font-size: 14px;
  line-height: 1.2;
  color: $form-input-color;
  cursor: pointer;
  background: transparent;

  &:after {
    position: absolute;
    top: 50%;
    right: $form-element-padding;
    margin-top: -1px;
    border-top: 4px solid $form-input-border;
    border-right: 4px solid transparent;
    border-left: 4px solid transparent;
    content: "";
  }

  select {
    z-index: 2;
    width: 100%; // needed for Firefox
    max-width: 100%;
    min-width: inherit;
    padding: $form-element-padding;
    cursor: pointer;
    border: 1px solid $form-input-border;
    -webkit-appearance: none;

    .ie & {
      height: 100%; // needed for IE
    }
  }
}

.icon-checkbox-checked {
  position: absolute;
  top: 50%;
  left: 50%;
  display: none;
  transform: translate(-50%, -50%);

  path {
    fill: $container-background-color-opaque;
  }
}

.form-captcha-description {
  margin-top: 5px;
  font-size: $body-font-size * 0.9;
  font-style: italic;
}

.form-hidden {
  display: none;

  .lt-ie10 & {
    display: block;
  }
}

.form-stars {
  @extend %clearfix;
  display: inline-block;
  padding: 0;
  margin: 0;
  border: 0;

  &.form-field-error {
    position: relative;
    padding-bottom: 24px;
  }

  .input-star {
    float: right;
    padding-right: 4px;
    line-height: 1;
    color: $accent-color-light;
    cursor: pointer;

    &:before {
      color: $accent-color-light;
    }

    &:hover,
    &:hover ~ .input-star {

      &:before {
        color: $body-color;
      }
    }
  }

  input:checked {
    ~ label {
      &:before{
        color: $body-color;
      }
    }
  }

  .form-inline-message {
    position: absolute;
    bottom: 0;
    left: 0;
    white-space: nowrap;

    + .form-inline-message {
      display: none;
    }
  }
}

.sr-only {
  @extend %show-for-sr;
}

//----- Form Errors & Messages -----//
.form-field-error {
  .form-input {
    border-color: $error-color-border;

    &::placeholder {
      color: $error-color-placeholder;
    }
  }
}

.form-inline-message {
  display: block;
  padding: 5px 0 0;
  font-size: 12px;
  color: $error-color;
  transition: all 0.5s $ease-out-quad;

  &:first-letter {
    text-transform: uppercase;
  }
}

/* scss-combine:core/swatch */
// none swatch value for non-required swatches
.form-field-swatch {
  .swatch-none {
    .swatch {
      overflow: hidden;
      border: 1px solid rgba(#000, 0.15);

      svg {
        position: absolute;
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
      }
    }
  }
}


// Modules
/* scss-combine:core/account */
// Default Variables

$account-link-color: #888 !default;
$account-light-color: #fff !default;
$account-accent-color: #5dc5e3 !default;
$account-border-color: #e4e4e4 !default;
$account-gutter: 30px !default;
$account-font-size: 15px !default;
$account-border-radius: 0 !default;
$account-text-color: #5f5f5f !default;

$account-card-background-color: #f5f5f5 !default;

$giftcard-nav-use-default-styles: true !default;

$account-breakpoint-x-small: 400px !default; // the account table columns change to full-width
$account-breakpoint-small: 620px !default; // menu changes from full-width to column
$account-breakpoint-medium: 900px !default; // menu changes from 35% to 25% width

// Internal Variables

$account-font-size-heading: $account-font-size + 2 !default;

$account-text-color-light: lighten($account-text-color,20%) !default;

$account-gutter-inner: $account-gutter / 2 !default;

$auth-gutter: $account-gutter !default;
$auth-font-size: $account-font-size !default;

// Imports

/* scss-combine:account/components/layout */
.account-wrapper {
  @extend %clearfix;
  font-size: $account-font-size;
}

.account-nav,
.account-body {
  vertical-align: top;
}

.account-body {
  @media screen and (min-width: $account-breakpoint-small) {
    float: right;
    width: 65%;
    padding-left: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 75%;
  }

  > :first-child {
    margin-top: 0;
  }

  .account-nav-hidden & {
    float: none;
    width: 100%;
    padding-left: 0;
    margin-left: 0;
  }
}

.account-nav {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    float: left;
    width: 35%;
    padding-right: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-medium) {
    width: 25%;
  }
}

/* scss-combine:account/components/forms */
.account-wrapper {
  .form-label-text {
    display: inline-block;
    width: 100%;
    margin-bottom: 5px;
  }

  .form-field-checkbox,
  .form-field-radio {
    .form-label-text { width: auto; }
  }

  .form-field {
    margin-bottom: $account-gutter-inner;
  }

  .form-input {
    width: 100%;

    &.account-form-half {
      width: 50%;
    }
  }

  .form-actions {
    clear: both;
  }
}

.account-form-column-container {
  @extend %clearfix;

  .form-field {
    @media screen and (min-width: 480px) {
      float: left;
      width: 50%;

      &:nth-child(odd) { padding-right: $account-gutter-inner; }
      &:nth-child(even) { padding-left: $account-gutter-inner; }
    }
  }
}

/* scss-combine:account/components/table */
.account-wrapper {
  table {
    width: 100%;
    margin-top: $account-gutter-inner;
    margin-bottom: $account-gutter-inner;
  }

  td,
  th {
    padding: $account-gutter-inner;
  }
}

.account-table-tight {
  th,
  td {
    padding: 10px 0;
    border-bottom: 1px solid $account-border-color;
  }
}

/* scss-combine:account/components/list */
// Layout
// Each list item has 3 sections: thumbnail, body, and badge.

.account-items {
  margin-bottom: $account-gutter;
}

.account-item {
  position: relative;
  display: table;
  width: 100%;
  border-bottom: 1px solid $account-border-color;

  &:first-of-type {
    border-top: 1px solid $account-border-color;
  }
}

.account-item-thumbnail,
.account-item-body,
.account-item-badge,
.account-item-actions {
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-x-small) {
    display: table-cell;
    vertical-align: top;
  }
}

// Link
// Makes the entire item clickable

.account-item-link {
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  z-index: 100;
}

// Thumbnail

.account-item-thumbnail {
  min-width: 100px;

  img {
    height: auto;
    max-width: 100px;
  }
}

// Body

.account-item-body {
  width: 100%;

  @media screen and (min-width: $account-breakpoint-x-small) {
    .account-item-thumbnail + & {
      padding-left: $account-gutter-inner;
    }
  }
}

.account-item-title {
  margin-top: 3px;
  font-size: $account-font-size;

  a {
    color: inherit;
  }
}

.account-item-title-large {
  font-size: $account-font-size * 1.2;
}

.account-item-description {
  margin-top: 0;
  margin-bottom: 5px;
  color: $account-text-color;
}

.account-item-details {
  @extend %clearfix;
  font-size: $account-font-size;
  color: $account-text-color;
}

.account-item-detail {
  float: left;
  margin-top: $account-gutter-inner;
  margin-right: $account-gutter;
}

//scss-lint:disable VariableForProperty
.account-item-detail-title {
  display: block;
  margin-top: 0;
  margin-bottom: 3px;
  font-size: 12px;
  font-weight: normal;
  letter-spacing: 1px;
  color: mix($account-light-color,$account-text-color,60%);
  text-transform: uppercase;
}

// Badge

.account-item-badge,
.account-item-actions {
  white-space: nowrap;
}

/* scss-combine:account/components/header */
.account-header {
  display: table;
  width: 100%;
  margin-bottom: $account-gutter-inner;
}

.account-heading,
.account-toolbar {
  display: table-cell;
  vertical-align: baseline;
}

.account-toolbar {
  text-align: right;

  * {
    display: inline;
  }
}

.account-tool {
  position: relative;
  z-index: 10;
  display: inline-block;
  padding: 0;
  background: transparent;
  margin-left: 10px;
  cursor: pointer;
  color: inherit;
  border: 0;

  &:hover { color: $account-link-color; }

  &:focus { outline: 0; }
}

/* scss-combine:account/components/navigation */
.account-nav-list {
  padding: 0;
  margin-bottom: $account-gutter-inner;
  list-style: none;
  border: 1px solid $account-border-color;
  border-radius: $account-border-radius;
}

.account-nav-item {
  color: $account-link-color;
  border-top: 1px solid $account-border-color;

  &:first-child {
    border-top: 0;
  }

  &.is-active {
    color: $account-accent-color;
    border-left: 3px solid $account-accent-color;
  }
}

.account-nav-link {
  display: block;
  padding: 15px;
  color: inherit;
}

.account-nav-badge {
  float: right;
}

/* scss-combine:account/components/pagination */
.account-wrapper .pagination {
  margin-top: $account-gutter-inner;
}

/* scss-combine:account/components/card */
%account-card-style {
  background: $account-card-background-color;

  .form-input {
    background-color: $account-light-color;
  }
}

.account-card {
  @extend %account-card-style;
  padding: $account-gutter-inner;
  margin: $account-gutter-inner 0;
  border-radius: $account-border-radius;
}

/* scss-combine:account/components/toggle */
// Set the initial toggle state to hidden
.account-toggle-hidden {
  display: none;
}

/* scss-combine:account/components/status */
// Status
// A label used to mark the status of an item

.account-status {
  padding: 6px 10px 5px;
  margin: 0;
  font-size: 12px;
  font-weight: normal;
  line-height: 1;
  letter-spacing: 1px;
  color: $account-light-color;
  text-align: center;
  text-transform: uppercase;
  background: #8e8e8e; // TODO: Colors for each status
}

/* scss-combine:account/components/shipments */
// Shipments
// A list of shipments made for an order. Shown on the Order Details page.

.account-shipments {
  margin-bottom: $account-gutter;
}

.account-shipments-table {
  th,
  td {
    padding: 10px 0;
    text-align: left;
  }

  th {
    padding: $account-gutter-inner 0 0;
    border-top: 1px solid $account-border-color;
  }

  // Override default margins
  .account-wrapper & {
    margin-top: 0;
    margin-bottom: 0;
  }
}


/* scss-combine:account/pages/address */
// Address List

.account-page-add-address {
  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-address-detail {
  display: block;
}

// Add Address

.account-address-form {
  padding-top: $account-gutter-inner;

  @media screen and (min-width: $account-breakpoint-medium) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }

      &:nth-of-type(2n + 1){
        clear: left;
      }
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}

/* scss-combine:account/pages/orders-receipt */
.account-page-orders-receipt .account-content {
  padding-top: $account-gutter-inner;
  border-top: 1px solid $account-border-color;
}

// Summary

.account-receipt-summaries {
  @extend %clearfix;
}

.account-receipt-summary {
  float: left;
  width: 33%;
  padding-right: $account-gutter;
}

.account-receipt-summary-title {
  margin-top: 5px;
  margin-bottom: 10px;
  font-size: $account-font-size;
}

.account-receipt-summary-address {
  line-height: 1.5;
}

.account-receipt-detail {
  margin-bottom: 5px;
}

.account-receipt-detail-title {
  color: $account-text-color-light;
}

// Comments

.account-receipt-comments {
  white-space: pre-line;
}

// Receipt Items (products)

.account-receipt-items {
  padding-top: $account-gutter-inner;
}

.account-receipt-download {
  margin: 10px 0;
}

// Totals

.account-receipt-totals {
  display: table;
  width: 50%;
  margin-left: 50%;
}

.account-receipt-total {
  display: table-row;
  margin-bottom: $account-gutter-inner;
}

.account-receipt-total-title,
.account-receipt-total-value {
  display: table-cell;
  padding-top: $account-gutter-inner;
  padding-bottom: $account-gutter-inner;
  border-top: 1px solid $account-border-color;

  .account-receipt-total:first-of-type & {
    border-top: 0;
  }
}

.account-receipt-total-title {
  font-weight: normal;
  letter-spacing: 1px;
  color: $account-text-color-light;
}

.account-receipt-total-value {
  text-align: right;
}

/* scss-combine:account/pages/recent-items */

/* scss-combine:account/pages/returns */
// Return form

.account-return-table {
  th {
    text-align: left;

    &:last-of-type {
      text-align: right;
    }
  }

  .form-field {
    margin-bottom: 0;
  }
}

.account-return-fields {
  margin-top: $account-gutter;

  .form-field-select {
    width: 50%;
  }
}

/* scss-combine:account/pages/wishlists */
.account-wishlists {
  margin-bottom: 30px;
}

/* scss-combine:account/pages/messages */
// Listing

.account-messages-table.table-clean {
  border-bottom: 1px solid $account-border-color;

  td {
    padding-right: 0;
    padding-left: 0;
    vertical-align: top;
    border-top: 1px solid $account-border-color;
  }
}

.account-messages-table-date {
  width: 22%;
  text-align: right;
}

.account-message-subject {
  margin-top: 0;
  margin-bottom: 10px;
  font-size: $account-font-size-heading;
  font-weight: 500;

  .account-message-is-reply &:before {
    padding-right: 5px;
    content: "\21c9";
  }

  .account-message-is-read & {
    color: $account-text-color-light;
  }
}

.account-message-content {
  padding-right: $account-gutter;
  font-size: $account-font-size;
  line-height: 1.5;
  color: $account-text-color;
  white-space: pre-line;

  .account-message-is-read & {
    color: lighten($account-text-color-light,10%);
  }
}

.account-message-date {
  color: $account-text-color-light;
}

// Form

.account-messages-form-subject {
  width: 50%;
}

/* scss-combine:account/pages/download */
// List

.account-download {
  padding: $account-gutter-inner 0 0;
  margin: $account-gutter-inner 0;
  border-top: 1px solid $account-border-color;
}

.account-download-title {
  margin: 0;
  font-size: 1em;
}

.account-download-link {
  color: inherit;
  text-decoration: underline;
}

.account-download-filename {
  font-size: 1.2em;
}

.account-download-filesize {
  padding-left: 5px;
  font-weight: normal;
  color: $account-text-color-light;
}

.account-download-description {
  padding-top: 10px;
  padding-bottom: 10px;
}

.account-download-expiry {
  font-size: 0.9em;
  color: $account-text-color-light;
}

.account-download-expiry-item {
  display: block;
  padding-bottom: 5px;

  &:last-of-type {
    padding-bottom: 0;
  }
}

// Gallery

.account-downloads-gallery-image {
  text-align: center;
}

.account-downloads-gallery-thumbs {
  @extend %clearfix;
  text-align: center;
}

.account-downloads-gallery-thumb {
  display: inline-block;
  width: 10%;
  height: 10%;
  padding: 0 2px;
  vertical-align: middle;
  cursor: pointer;

  img {
    width: 100%;
    height: auto;
  }
}

/* scss-combine:account/pages/invoice */
.account-invoice-date {
  margin-top: -20px;
  font-size: $account-font-size;
}

// Address

.account-invoice-addresses {
  @extend %clearfix;
  margin-top: $account-gutter;
  margin-bottom: $account-gutter;
}

.account-invoice-address {
  float: left;
  margin-right: $account-gutter;
}

.account-invoice-address-title {
  margin-top: 5px;
  margin-bottom: 10px;
  font-size: $account-font-size;
}

.account-invoice-address-body {
  line-height: 1.5;
}

// List

.account-invoice-table {
  .account-wrapper & {
    margin-bottom: 0;
  }

  th,
  td {
    padding-right: $account-gutter-inner;
    text-align: left;
    vertical-align: top;

    &:nth-of-type(0n+3) {
      width: 10%;
    }

    &:last-of-type {
      width: 160px;
      padding-right: 0;
      text-align: right;
    }
  }

  td:first-of-type {
    width: 60px + 25px;
  }
}

.account-invoice-item-thumbnail {
  width: 60px;
  height: auto;
}

.account-invoice-item-name {
  margin-bottom: 5px;
  font-weight: bold;
}

.account-invoice-total-title {
  font-weight: bold;
}

// Totals

.account-invoice-totals {
  .account-wrapper & {
    width: auto;
    margin-top: 0;
    margin-left: auto;
  }

  th,
  td {
    padding-right: $account-gutter-inner;

    &:last-of-type {
      width: 160px;
      padding-right: 0;
      text-align: right;
    }
  }
}

/* scss-combine:account/pages/settings */
.account-page-settings {
  .alert:first-of-type {
    margin-top: 0;
  }

  .account-content {
    padding-top: $account-gutter-inner;
    border-top: 1px solid $account-border-color;
  }
}

.account-settings-form {
  @extend %clearfix;
  padding-top: $account-gutter-inner;

  .form-field {
    float: left;
    width: 50%;
    padding-right: $account-gutter-inner / 2;
    vertical-align: top;

    &:nth-of-type(2n+0) {
      padding-right: 0;
      padding-left: $account-gutter-inner / 2;
    }
  }

  .form-actions {
    padding-top: $account-gutter-inner;
  }
}


/* scss-combine:account/pages/auth-login */

.account-page-login .alert {
  max-width: 860px;
  margin-top: 0;
  margin-right: auto;
  margin-bottom: $account-gutter;
  margin-left: auto;
}

.account-login-wrapper {
  max-width: 860px;
  margin-right: auto;
  margin-left: auto;

  @media screen and (min-width: $account-breakpoint-small) {
    display: table;
    width: 100%;
    table-layout: fixed;
  }
}

.account-page-login .account-sub-heading {
  margin: 0 0 20px;
}

.account-login,
.account-create {
  @media screen and (min-width: $account-breakpoint-small) {
    display: table-cell;
    vertical-align: top;
  }
}

// Login Form

.account-login {
  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    padding: $account-gutter-inner (30px + $account-gutter-inner) $account-gutter-inner 0;
  }
}

.account-login-button {
  margin-right: $account-gutter-inner;
}

// New Account

.account-create {
  padding: $account-gutter-inner;
  background: $account-card-background-color;
}

.account-create-intro {
  &:empty {
    display: none;
  }
}

.account-create-facts {
  padding-left: 20px;
  margin-bottom: 30px;
  list-style: disc;

  li:empty {
    display: none;
  }
}

/* scss-combine:account/pages/auth-forgot */
.account-page-forgot .account-body {
  width: 100%;
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
  text-align: center;
  float: none;
}

.account-forgot-form {
  display: table;
  width: 100%;
  margin-top: $account-gutter;

  .form-field,
  .form-actions {
    display: table-cell;
    text-align: left;
    vertical-align: bottom;
  }

  .form-actions {
    width: 1px;
    padding-left: 10px;
  }
}

/* scss-combine:account/pages/auth-create */
.account-page-create {
  .account-header {
    padding-bottom: $account-gutter-inner;
    margin-bottom: $account-gutter;
    border-bottom: 1px solid $account-border-color;
  }

  .account-body {
    max-width: 860px;
    margin-right: auto;
    margin-left: auto;
  }
}

.account-create-form {
  .g-recaptcha > div {
    margin: 0 auto;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    .form-field {
      float: left;
      width: 50%;
      padding-right: $account-gutter-inner;

      &:nth-of-type(2n) {
        padding-right: 0;
        padding-left: $account-gutter-inner;
      }

      &:nth-of-type(2n + 1) {
        clear: left;
      }
    }
  }
}

.account-create-details,
.account-create-address {
  @extend %clearfix;
  padding-bottom: $account-gutter-inner;
  margin-bottom: $account-gutter;
  border-bottom: 1px solid $account-border-color;
}

.account-create-form-actions {
  text-align: center;
}

.account-create-captcha {
  display: inline-block;
  vertical-align: middle;

  @media screen and (max-width: $account-breakpoint-small - 1) {
    margin-bottom: $account-gutter-inner;
  }

  @media screen and (min-width: $account-breakpoint-small) {
    margin-right: $account-gutter-inner;
  }
}

/* scss-combine:account/pages/auth-created */
.account-page-created .account-body {
  float: none;
  width: 100%;
  max-width: 600px;
  margin-right: auto;
  margin-left: auto;
  text-align: center;

  p {
    margin-bottom: $account-gutter;
    line-height: 1.5;
  }

  em {
    font-family: monospace;
    font-style: normal;
    font-weight: bold;
    background: $account-card-background-color;
  }
}

/* scss-combine:account/pages/auth-new-password */
.account-page-new-password .account-content {
  width: 100%;
  max-width: 400px;
  margin-right: auto;
  margin-left: auto;
}

.new-password-requirements {
  margin-bottom: $account-gutter;
  text-align: center;
}


/* scss-combine:account/pages/gift-certificate */
// (optional) gift card nav

@if ($giftcard-nav-use-default-styles) {
  .giftcard-nav {
    padding: $account-gutter * 2;
    margin-bottom: $account-gutter * 2;
    text-align: center;
  }

  .giftcard-nav-item {
    display: inline-block;
    margin: 0 1em;
    color: $account-link-color;

    &.is-active { color: $account-text-color; }
  }
}

.giftcard-heading-container {
  margin-bottom: $account-gutter * 2;
  text-align: center;
}


// Purchase

.giftcard-form-help-message {
  display: block;
  margin-top: 6px;
}

.giftcard-form-preview {
  iframe { width: 100%; }
}

.giftcard-single-theme {
  display: none;
}


// Check balance

.giftcard-balance-container {
  max-width: 600px;
  margin: 0 auto;
}

.form-giftcard-balance {
  position: relative;

  .form-field-title {
    position: absolute;
    left: -9999px;
  }

  .form-field-control {
    display: block;

    @media screen and (min-width: 600px + ($account-gutter-inner * 2)) {
      margin-right: calc(30% + #{$account-gutter-inner});
    }
  }

  .form-actions {
    @media screen and (min-width: 600px + ($account-gutter-inner * 2)) {
      position: absolute;
      top: 0;
      right: 0;
      width: 30%;
    }
  }

  .button {
    width: 100%;
  }
}


// Redeem

.giftcard-redeem-steps {
  line-height: 1.5;

  li { margin-bottom: 0.75em; }
}


/* scss-combine:core/unavailable */
.unavailable-container {
  display: table;
  text-align: center;
  width: 100%;
  height: 100vh;
  font-size: 14px;

  @media (min-width: 400px) {
    font-size: 16px;
  }
}

.unavailable-container-inner {
  display: table-cell;
  padding: 20px;
  vertical-align: middle;
}

.unavailable-logo {
  margin-top: 0;
}

.unavailable-heading {
  font-size: 1.5em; // 24px desktop
}

.unavailable-message {
  max-width: 400px;
  margin: 0 auto;
  font-size: 0.875em; // 14px desktop
}


// Templates
/* scss-combine:components/header */
.main-header {
  @if ($enable-main-nav-background-color == "true") {
    @if ($always-show-main-nav-background-color == "true") {
      .no-carousel & {
        background-color: $main-nav-background-color-opaque;
      }
    }
  }

  .sticky-header & {
    top: 0;
    width: 100%;
  }

  .alternate-header & {

    @if ($enable-main-nav-background-color == "true") {
      background-color: $main-nav-background-color-opaque;
    }

    @include breakpoint (l-up) {
      top: -200px;
    }
  }

  .alternate-header.header-positioned & {
    position: fixed;
  }

  .alternate-header.sticky-header & {
    top: 0;
  }

  .main-nav-open & {
    background-color: transparent;
  }

  @include breakpoint(print) {
    position: relative !important;
  }
}

.header-container {
  @extend %clearfix;
  display: table;
  padding-right: 0;
  padding-left: 0;
  table-layout: fixed;

  .main-nav-open & {
    max-width: 100%;
  }
}

.header-branding,
.header-navigation {
  position: relative;
  display: table-cell;
  float: left;
  height: $header-height-mobile;
  max-height: $header-height-mobile;
  vertical-align: middle;
  box-sizing: border-box;
  transition: width 0.5s $ease-out-quad;

  @include breakpoint(l-up) {
    height: $header-height-desktop;
    max-height: $header-height-desktop;

    .alternate-header & {
      height: $header-height-mobile;
      max-height: $header-height-mobile;
    }
  }
}

.header-branding {
  width: 70%;
  padding-right: $gutter;
  padding-left: $gutter;

  .alternate-header & {
    @include breakpoint(l-up) {
      width: 30%;
    }
  }
}

.header-navigation {
  float: right;
  width: 30%;
  transition: width 0.5s $ease-out-quad;

  @include breakpoint(l-up) {
    padding-right: $gutter;
  }

  .alternate-header & {
    @include breakpoint(l-up) {
      width: 70%;
    }
  }

  @include breakpoint(print) {
    display: none;
  }
}

.header-logo {
  position: relative;
  display: inline-block;
  height: 100%;
  max-width: 100%;
  max-height: 100%;
  box-sizing: border-box;

  @include breakpoint(l-up) {

    .alternate-header & {
      transform: translateY(0);
    }
  }
}

.header-logo-text,
.header-logo-image {
  position: absolute;
  display: none;
  max-height: 100%;
  margin: 0;
  opacity: 0;
  transition: all 1s $ease-out-quad;

  &.visible {
    position: relative;
    display: block;
    opacity: 1;
  }
}

.header-logo-image {
  top: 50%;
  max-width: $header-logo-max-width;
  padding-top: $gutter;
  padding-bottom: $gutter;
  transform: translateY(-50%);
}

.header-logo-text {
  z-index: 10;
  width: 100%;
  overflow: hidden;
  font-family: $store-title-font;
  font-size: 20px;
  font-weight: $store-title-weight;
  line-height: 60px;
  color: $store-title-color;
  text-overflow: ellipsis;
  text-transform: uppercase;
  word-wrap: break-word;
  white-space: nowrap;

  @include breakpoint(l-up) {
    font-size: 26px;
  }

  &.no-image {
    top: 50%;
    height: auto;
    max-height: inherit;
    transform: translateY(-50%);

    .alternate-header & {
      top: 0;
      transform: translateY(0);
    }
  }
}

.main-header-menu {
  padding-top: $gutter;
  padding-bottom: $gutter;

  .alternate-header & {
    width: 100%;
    padding-top: $header-padding;
    padding-bottom: $header-padding;
  }

  .alternate-header.main-nav-open & {
    padding-top: $gutter;
    padding-bottom: $gutter;
  }
}

.navigation-toggle-mobile-wrapper {
  @extend %border-radius-treatment;
  position: absolute;
  top: 50%;
  right: $gutter;
  display: inline-block;
  padding: 23px;
  margin-top: -23px;
  cursor: pointer;
  background-color: transparent;
  border: 0;
  outline: 0;
  transition: all 0.1s $ease-out-quad;

  @include breakpoint(l-up) {
    display: none;
  }
}

.navigation-toggle-mobile {
  @extend %navigation-toggle-line;
  position: absolute;
  top: 22px;
  left: 10px;

  &:before,
  &:after {
    @extend %navigation-toggle-line;
    position: absolute;
    left: 0;
    content: " ";
  }

  &:before { top: 6px; }
  &:after { top: -12px; }

  &.expand {
    left: 13px;
    background: transparent;
    border-top: 0;

    &:before { transform: rotate(45deg); }
    &:after { transform: rotate(-45deg); }

    &:before,
    &:after {
      top: 0;
      width: 18px;
    }
  }
}

