/* scss-combine:pages/compare */
.compare-container {
  padding-top: $page-padding-top;
  padding-bottom: $page-padding-bottom;
  color: $body-color-dark;
}

.compare-grid-row {
  @include grid-row(nest);
}

.compare-grid-item {
  @include grid-columns(1 of 1, collapse);
  position: relative;
  padding-right: $gutter;
  padding-left: $gutter;
  transition: all 0.2s $ease-out-quad;

  @include breakpoint(m-up) {
    @include grid-columns(1 of 3, collapse);
  }

  .compare-layout-4 & {
    @include grid-columns(1 of 4, collapse);
  }

  .compare-row-product-collapse & {
    @include breakpoint(m-down) {
      @include grid-columns(1 of 2, collapse);
    }
  }

  .compare-row-information-collapse & {
    padding-top: $gutter;
    padding-bottom: $gutter;

    @include breakpoint(m-down) {
      @include grid-columns(1 of 1, collapse);
      float: right;
      padding-top: $gutter/2;
      padding-bottom: $gutter/2;
      clear: both;
    }

    &:before {
      display: block;
      line-height: 120%;
      color: $body-color;
      text-decoration: underline;
      content: attr(data-product-name);

      @include breakpoint(l-up) {
        display: none;
      }
    }
  }

  &.compare-grid-empty {

    @include breakpoint(m-down) {
      display: none;
    }

    &:before {
      display: none;
    }
  }
}

// Single Item
