  font-size: 14px;
}

.blog-item-posted-date {

  .blog-item-normal & {
    display: block;
    margin-bottom: $gutter/2;
  }

  .blog-item-square & {
    margin-top: $gutter/2;
    margin-bottom: 0;
  }

}

.blog-item-read-more {
  text-decoration: underline;
}

/* scss-combine:components/blog/blog-post */
.blog-post-footer {
  margin-top: $gutter*2;
  font-size: 14px;
  line-height: 1.3;
}

.blog-post-tag {
  font-size: 14px;
  text-decoration: underline;
}

.blog-share {
  margin-top: $gutter/2;

  .sharing-button {
    display: inline-block;
    width: 20px;
    height: 20px;
    font-size: 14px;
    line-height: 20px;
    text-align: center;
    background: transparent;
  }

  .facebook-share-button {
    line-height: 20px;
  }

  .fb-like {
    display: block;
    margin-top: -3px;
  }

  i {
    color: $body-color;

    &:hover {
      color: $body-color-link;
    }
  }
}

/* scss-combine:components/cart/cart-item */

.cart-item {
  margin-top: $gutter*2;

  &:first-child {
    margin-top: 0;
  }
}

.cart-item-top {
  position: relative;
  padding: $gutter / 2;
  background-color: rgba($color-primary-contrast, 0.2);

  @include breakpoint(l-up) {
    padding: $gutter;
  }
}

// Cart item actionable elements
.cart-item-actions {
  position: absolute;
  right: 0;
  bottom: 0;
  display: inline-block;
}

.cart-item-action {
  display: inline-block;
  float: left;
  width: 50px;
  height: 50px;
  font-size: 18px;
  line-height: 1;
  color: $body-color;
  background: $body-color-light;
  border: 0;
  outline: none;
  transition: 1s all $ease-out-quad;

  &:hover {
    color: $body-color-link;
    @include hover($body-color-light, background-color);
  }

  &.quantity-input {
    font-size: 30px;
    color: $body-color-light;
    cursor: default;
    background-color: $body-color-dark;
  }
}

.cart-item-remove {
  position: absolute;
  top: 0;
  right: 0;
}

// Cart item display elements
.cart-item-image {
  @include maintain-ratio(2 1);
  background: transparent 50% 50% no-repeat;
  background-size: contain;
}

.cart-item-content {
  @extend %clearfix;
  position: relative;
  margin-top: $gutter*2;
}

.cart-item-label {
  text-decoration: underline;
}

.cart-item-meta {

  @include breakpoint(m-up) {
    @include grid-columns(12 of 12, row);
    padding-right: $gutter*2;
  }
}

.cart-item-product-name {
  margin: 0;

  .cart-item-product-title { color: inherit; }

  @include breakpoint(m-up) {
    max-width: percentage(8 / 10);
  }
}

.cart-item-single-price {
  margin: 0;
  font-size: 18px;
}

.cart-item-price-discount {
  margin-left: 5px;
}

.cart-item-price-discounted {
  text-decoration: line-through;
}

.cart-item-price-block {
  display: inline-block;
  font-size: 20px;
  line-height: 1;
  color: $heading-color;

  @include breakpoint(m-up) {
    position: absolute;
    top: 0;
    right: 0;
  }
}

//TODO: THIS IS VERY SIMILAR TO SINGLE PRODUCT
.cart-item-details-block {
  @extend %clearfix;
  padding: 0 0 $gutter/2 0;
  margin: 0 0 $gutter/2 0;
  font-size: 13px;
}

.cart-item-details-name,
.cart-item-details-value {
  float: left;
  margin: 0;
}

.cart-item-details-name {
  margin-right: $gutter/2;
  clear: left;
  color: $meta-color;
}

/* scss-combine:components/cart/cart-totals */
.cart-totals {
  margin: $gutter * 2 0;
}

.cart-total {
  margin-top: $gutter;
  font-size: 14px;
}

.cart-total-value {
  text-align: right;
}

.cart-total-label,
.cart-total-value {
  display: inline-block;
  width: 49%;
  vertical-align: middle;
  box-sizing: border-box;

  @include breakpoint(m-down) {
    width: 100%;
    text-align: left;
  }
}

.cart-grand-total-label {
  display: block;
}

.cart-grand-total-value {
  display: block;
  font-family: $heading-font;
  font-size: 30px;
  color: $body-color-dark;
}

.cart-add-coupon {
  text-decoration: underline;
}

/* scss-combine:components/cart/cart-coupons */
.coupon-code-form {
  position: relative;

  .coupon-entry-field {
    margin-bottom: $form-gutter/2;

    @include breakpoint(m-only) {
      float: left;
    }
  }

  .coupon-entry-submit {
    @include breakpoint(m-only) {
      float: left;
      margin-left: $form-gutter;
    }

    .button {
      line-height: 16px;
    }
  }
}

.cart-total-coupon {
  vertical-align: middle;
}

.coupon-code-remove {
  @extend %meta-text-treatment;
  font-size: 12px;
}

/* scss-combine:components/cart/cart-shipping */
.shipping-quotes,
.shipping-calculator-form {
  @include breakpoint(m-only) {
    @include grid-columns(1 of 2, collapse);
  }
}

.shipping-quotes {

  @include breakpoint(m-only) {
    padding-left: $gutter/2;
  }

  @include breakpoint(l-up) {
    margin-top: $gutter*2.5;
  }

  .form-inline {
    margin-bottom: $form-gutter/2;
  }
}

.shipping-calculator-form {
  @include breakpoint(m-only) {
    padding-right: $gutter/2;
  }

  .form-input {
    min-width: inherit;
  }
}

.shipping-method-badge {
  display: inline-block;
  max-height: 100%;
  line-height: 1;
  vertical-align: middle;
}

/* scss-combine:components/cart/cart-toggles */
.certificate-toggle,
.item-gift-wrap-toggle,
.shipping-calculator-toggle,
.coupon-code-toggle {
  padding: 0;
  color: $body-color-dark;
  text-decoration: underline;
  background: transparent;
  border: 0;
  outline: 0;
  transition: all 0.5s $ease-out-quad;

  &:hover {
    @include hover($body-color-dark);
  }

  &.disabled {
    color: $accent-color-light;
    opacity: 0.8;
  }
}

.certificate-form,
.gift-wrap-form-wrapper,
.shipping-calculator,
.coupon-code-form {
  @include grid-columns(1 of 1, collapse);
  transition: all 1s $ease-out-quad;

  &.animating-out,
  &.hidden {
    height: 0;
    overflow: hidden;
    opacity: 0;
    visibility: hidden;
  }

  &.animating {
    overflow: hidden;
  }

  &.visible {
    padding-top: $gutter/2;
    padding-bottom: $gutter/2;
    opacity: 1;
  }

  .form-input,
  .form-field {
    font-size: 14px;
  }
}

.coupon-code-form,
.gift-wrap-form-wrapper {
  .form-input {
    min-width: initial;
  }
}

/* scss-combine:components/cart/gift-wrapping */

.item-gift-wrap {
  margin-top: $gutter;
}

.item-gift-wrap-toggle {
  font-size: 14px;
}

.item-gift-wrap-change {
  display: block;
  margin-top: $gutter/2;
  margin-bottom: $gutter;
}

.item-gift-wrap-info {
  font-size: 16px;

  + .gift-wrap-form-wrapper {
    margin-top: $gutter/2;
  }
}

.gift-wrap-instructions {
  margin-top: 0;
}

.item-gift-wrap-add,
.gift-wrap-single,
.gift-wrap-multiple,
.gift-wrap-message,
.gift-wrap-image-toggle {
  display: none;

  &.visible {
    display: block;
  }
}

.gift-wrap-image-toggle {
  margin-top: 10px;
  font-size: 14px;
  color: $accent-color;
  text-decoration: underline;

  &:hover {
    @include hover($accent-color);
  }
}

.gift-wrap-image-wrapper {
  display: none;
}

.gift-wrap-image {
  display: block;
  margin: 0 auto;
}

.gift-wrap-multiple {
  @include grid-row();
}

.gift-wrap-item {

  .rows-of-3 & {
    @include breakpoint(s-only) {
      @include grid-columns(1 of 1);
    }

    @include breakpoint(m-only) {
      @include grid-columns(1 of 2);
    }

    @include breakpoint(l-up) {
      @include grid-columns(1 of 3);
    }
  }

  .rows-of-2 & {
    @include breakpoint(s-only) {
      @include grid-columns(1 of 1);
    }

    @include breakpoint(m-up) {
      @include grid-columns(1 of 2);
    }
  }
}

/* scss-combine:components/cart/gift-certificates */
.cart-total-certificate {
  vertical-align: middle;
}

.cart-certificate-remove {
  @extend %meta-text-treatment;
  font-size: 12px;
}

.cart-item-gift-certificate-edit {
  margin-top: $gutter;
}

/* scss-combine:components/cart/apple-pay */
.apple-pay-checkout-button {
  display: none;
  max-height: em(64px);
  min-width: em(200px);
  min-height: em(32px);
  padding: em(20px);
  cursor: pointer;
  background: {
    position: 50% 50%;
    repeat: no-repeat;
    size: 100% 60%;
  }
  border-radius: 5px;
  box-sizing: border-box;

  @if $apple-pay-style == "white" {
    background-color: $color-white;
    background-image: -webkit-named-image(apple-pay-logo-black);
  } @else if $apple-pay-style == "white-border" {
    background-color: $color-white;
    background-image: -webkit-named-image(apple-pay-logo-black);
    border: 0.5px solid $color-black;
  } @else {
    background-color: $color-black;
    background-image: -webkit-named-image(apple-pay-logo-white);
  }

  .apple-pay-supported & { display: inline-block; }
}

/* scss-combine:components/faceted-search */
.faceted-search {
  @include breakpoint(m-only) {
    @include grid-row(nest);
  }
}

.faceted-search-overlay {
  @include page-overlay(map-get($zindex, overlay));
}

.faceted-search-filter {
  margin-bottom: 50px;

  @include breakpoint(m-only) {
    @include grid-columns(1 of 2)
  }

  &:last-child {
    margin-bottom: 0;
  }

  li a {
    transition: color 0.2s $ease-out-quad;

    &:hover {
      @include hover($meta-color);
    }
  }
}

.filter-type-range {
  font-size: 0;

  .form-field {
    display: inline-block;
    width: 35%;
    padding-right: 5px;
    margin-bottom: 0;
    font-size: $body-font-size;
    vertical-align: top;

    @include breakpoint(l-only) {
      width: 50%;
    }
  }

  .form-button {
    width: 30%;
    padding: 0;
    margin: 0;

    @include breakpoint(l-only) {
      width: 100%;
      margin-top: $gutter/2;

      .button {
        width: 100%;
      }
    }
  }
}

.faceted-search-filter-list {
  display: none;

  &.is-open {
    display: block;
  }

  > .faceted-search-item {
    display: none;
  }
}

.faceted-search-toggle,
.faceted-search-remove,
.faceted-search-more {
  float: right;
  margin-top: 3px;
  font-size: 11px;
  vertical-align: bottom;
  cursor: pointer;
}

.faceted-search-more {
  display: block;
  float: none;
  padding: 0;
  margin-top: 10px;
  font-family: $heading-font;
  letter-spacing: 0.05em;
  color: $accent-color;
  text-transform: uppercase;
  background-color: transparent;
  border: 0;

  .animating & {
    display: none;
  }

  .hidden {
    display: none;
  }
}

.faceted-search-item {
  .is-open & {
    display: block;
  }

  .icon {
    display: inline-block;
    width: 10px;
    height: 10px;
    margin-right: 2px;
  }

  a {
    font-size: 14px;
    font-weight: 300;
    color: $meta-color;
    cursor: pointer;
  }

  &.hidden {
    display: none;
  }

  &.visible {
    display: block;
  }
}

.faceted-search-item-count {
  // scss-lint:disable VariableForProperty
  margin-left: 7px;
  font-size: 13px;
  color: mix($body-background-color, $meta-color);
}

.faceted-search-range-min,
.faceted-search-range-max {

  .form-input {
    min-width: 0;
    padding: $gutter/2 $gutter;
  }
}

.faceted-search-range-max {
  text-align: right;
}

/* scss-combine:components/product/item */
.product-item {
  text-align: $product-item-align;
}

.product-item-link {
  display: block;
  transition: background 0.7s $ease-out-quad;

  .no-touch .product-item-thumbnail:hover & {
    background-color: $product-item-actions-background-color;
  }
}

.product-item-thumbnail {
  position: relative;
  display: block;
  width: 100%;

  img {
    display: block;
    width: auto;
    height: auto;
    max-width: 100%;
    max-height: map-get($product-image-small, height);
    margin: 0 auto;
    transition: opacity 0.7s $ease-out-quad;
    backface-visibility: hidden;
  }

  .no-touch & {
    &:hover img {
      opacity: 0.3;
    }
  }
}

.product-item-details {
  margin-top: $gutter;
}

.product-item-price,
.product-item-brand {
  display: block;
  margin: 0;
  font-size: 14px;
}

.product-item-title {
  margin: 5px 0;
  font-family: $product-item-title-font-family;
  font-size: $body-font-size;
  font-weight: $product-item-title-font-weight;
  line-height: 1;

  a {
    color: $product-item-title-color;
  }
}

.product-item-price {
  margin-top: 8px;
}

//-- Badges --//
.product-item-badges {
  @extend %font-smoothing;
  @extend %meta-text-treatment;
  position: absolute;
  top: 50%;
  left: 50%;
  display: block;
  opacity: 1;
  transform: translateY(-50%) translateX(-50%);
  transition: all 0.5s $ease-out-quad;

  .no-touch .product-item-thumbnail:hover & {
    top: 70%;
    opacity: 0;
  }
}

.product-item-out-of-stock,
.product-item-sale-tag {
  @extend %font-smoothing;
  @extend %meta-text-treatment;
  padding: 8px 16px;
  font-size: 14px;
  color: $product-item-sale-color;
  text-align: center;
  white-space: nowrap;
  pointer-events: none;
  background-color: $product-item-sale-background;
}

.product-item-out-of-stock {
  color: $product-item-sold-out-color;
  background-color: $product-item-sold-out-background;
}

/* scss-combine:components/product/item-actions */

.product-item-actions {
  position: absolute;
  top: 50%;
  left: 0;
  display: none;
  width: 100%;
  text-align: center;
  opacity: 0;
  transform: translateY(-50%);

  .no-touch .product-item-thumbnail:hover & {
    display: block;
    opacity: 1;
    transition: all 0.7s $ease-out-quad;
  }

  @include breakpoint(m-up) {
    left: 50%;
    display: inline-block;
    width: 100%;
    max-width: 200px;
    transform: translateY(-50%) translateX(-50%);
  }

  li {
    padding: ($gutter / 4) ($gutter / 2);
    margin: 0;
  }
}

.product-item-action {
  display: inline-block;
  padding: 3px 0;
  margin: 0 auto;
  font-size: 14px;
  line-height: 1.1em;
  color: $product-item-actions-color;
  text-align: center;
  text-transform: uppercase;
  cursor: pointer;
  transition: all 0.7s $ease-out-quad;
  border-bottom: 1px solid transparent;

  @include breakpoint(m-up) {
    font-size: 16px;
  }

  &:hover {
    @include hover($product-item-actions-color);
    border-bottom-color: $product-item-actions-color;
  }
}

/* scss-combine:components/product/price */

.price-saved,
.price-withouttax,
.price-withtax,
.product-call-for-price {
  display: block;
  font-size: 110%;
  line-height: 1.2;
  color: $color-primary-dark-dark;

  .product-item-price & {
    color: $body-color;
  }
}

.price-rrp {
  text-decoration: line-through;
}

.price-tax-label {
  padding-left: $gutter / 4;
}

/* scss-combine:components/product/single */
.product-container {
  padding-top: $gutter*2;
  padding-bottom: $gutter*2;
}

.products-videos-container,
.products-related-container,
.products-reviews-container {
  padding-top: $gutter*2;
  padding-bottom: $gutter*2;
  border-top: 1px solid $border-color;
}

.video-grid-container { padding-top: $gutter * 3; }

/* scss-combine:components/product/single-product-details */

.single-product-left {
  margin-bottom: $gutter / 2;

  @include breakpoint(m-only) {
    max-width: percentage(8 / 12);
  }

  @include breakpoint(l-up, true) {
    @include grid-columns(5 of 12, row);
    padding-right: $gutter * 2;
  }

  @include breakpoint(xl-up) {
    @include grid-columns(4 of 12, row);
  }
}

.single-product-rating {

  .rating-stars-readonly {
    display: inline-block;
    margin-right: $gutter;
  }

  .rating-total-votes {
    display: inline-block;
  }
}

.single-product-details-block,
.product-description-container,
.product-price {
  display: block;
  margin: $gutter 0;
}

.single-product-details-block {
  @extend %clearfix;
  font-size: 13px;
}

.product-details-heading {
  font-size: 14px;
  margin: 0 0 6px;
}

.product-detail-item {
  display: block;
  clear: both;

  &.single-product-detail-hidden {
    display: none;
  }
}

.single-product-details-name,
.single-product-details-value {
  float: left;
  margin: 0;
}

.single-product-details-name {
  margin-right: $gutter / 2;
  clear: left;
  color: $meta-color;
}

.product-message-area .alert,
.product-option-message-area .alert {
  margin-bottom: 1em;
}

.product-description-container {
  color: $color-primary-dark-dark;
}

.single-product-wishlist-form {
  margin-top: $gutter / 2;
}

.product-add-to-cart {
  margin-top: $gutter * 1.5;
}

//-- Product Customizations / Options --//
.product-form-quantity,
.product-customizations .form-field,
.product-options .form-field {
  margin-bottom: $gutter * .75;
}

.variation-preview {
  position: relative;
  display: block;
  height: em(50);
  padding: 0 em(14) 0 em(50);
  margin-top: 1em;
  margin-bottom: 1em;
  color: $form-input-color;
  border: 1px solid $border-color;
  border-radius: 3px;
  transition: color 0.4s $ease-out-quad;

  &:hover {
    color: $accent-color;
  }
}

.variation-preview-thumb-wrap {
  position: absolute;
  top: em(6);
  left: em(6);
}

.variation-preview-thumb {
  width: em(36);
  height: em(36);
  background-position: center center;
  background-size: cover;
}

.variation-preview-label {
  display: inline-block;
  font-size: 14px;
  line-height: em(50);
  vertical-align: middle;
}

/* scss-combine:components/product/single-product-gallery */
.single-product-right {
  margin-top: $column-gutter * 2;
  margin-right: auto;
  margin-left: auto;

  @include breakpoint(m-only) {
    max-width: percentage(8 / 12);
    margin-left: 0;
  }

  @include breakpoint(l-up, true) {
    @include grid-columns(6 of 12, row);
    margin-top: 0;
    float: right;
  }

  @include breakpoint(xl-up) {
    @include grid-columns(7 of 12, row);
    float: right;
  }
}

.product-images {
  @extend %clearfix;
  position: relative;

  @include breakpoint(m-down) {
    min-height: 350px;
  }

  .sharing-buttons {
    position: absolute;
    right: 0;
    z-index: 1;
    transition: right 0.4s $ease-out-quad;

    @include media($min: lower-bound($xlarge-range) + 100px) {
      right: -50px;
    }
  }

  .sharing-button {
    display: block;
    width: 50px;
    height: 50px;
    line-height: 50px;
    text-align: center;
  }

  .facebook-share-button {
    position: absolute;
    right: 0;
    display: block;
    padding-top: 5px;
    line-height: 1;
    text-align: right;
    vertical-align: middle;
    background: transparent;

    iframe {
      right: 0;
    }
  }
}

.product-slideshow-wrapper {
  @extend %clearfix;
  float: left;
  width: 100%;
  margin-bottom: $column-gutter * 2;
  overflow: hidden;
}

.flickity-viewport {
  transition: height 0.2s $ease-out-quad;
}

.single-product-image,
.single-product-slideshow {
  @include span-columns(12);
  position: relative;
  transition: transform 0.4s $ease-out-quad;

  img {
    display: block;
    width: auto;
    max-height: map-get($product-image-large, height);

    &:not(:first-child) { position: absolute; }
  }
}

.single-product-slideshow {
  img {
    margin: 0 auto;
  }
}

.single-product-slideshow-image {
  display: block;
  width: 80%;

  img {
    width: 100%;
    margin-right: 0;
    margin-left: 0;

    .isolate-product-slider-images & {
      width: auto;
      margin: 0 auto;
    }
  }

  .isolate-product-slider-images & {
    width: 100%;
    max-height: map-get($product-image-large, height);

    &:not(:first-child) { position: absolute; }
  }
}

.single-product-thumbnails {
  @include grid-row(nest);
  margin-top: $gutter;
}

.single-product-thumbnail-wrapper {
  @include grid-columns(3 of 12);
  margin-bottom: $gutter;
}

.single-product-thumbnail {
  @include maintain-ratio(1 1);
  position: relative;
  display: block;
  max-width: 100%;
  margin: 0 auto;
  cursor: pointer;
  background: 50% 50% no-repeat;
  background-size: cover;
  border: 1px solid transparent;

  &.active {
    border: 1px solid $accent-color;
  }
}

// Image zoom
.pswp {
  z-index: 5500;
}

.pswp__ui {
  z-index: 6000;
}

/* scss-combine:components/product/reviews */
.product-reviews-title {
  margin-bottom: 0;
}

.reviews-header-action {
  float: right;
  font-size: 14px;
  font-weight: 400;
  color: lightness-adjust($button-primary-background-color, $container-background-color, $heading-color);
  text-decoration: underline;
  text-transform: none;
  transition: all 1s $ease-out-quad;

  &:hover {
    @include hover($color-primary-dark);
  }

  &.disabled {
    color: $button-disabled-background-color;
  }
}

.review-grid-container {
  transition: all 1s $ease-out-quad;

  &.animating-out,
  &.hidden {
    //scss-lint:disable ImportantRule
    height: 0 !important;
    overflow: hidden;
    opacity: 0;
    visibility: hidden;
  }

  &.animating {
    overflow: hidden;
  }

  &.visible {
    padding-top: $gutter*3;
    opacity: 1;
  }
}

// Individual Reviews
.single-review-title {
  margin-top: $gutter/2;
  margin-bottom: $gutter/2;
}

/* scss-combine:components/product/reviews-modal */
// Review Modal Outer
.write-review-wrapper {
  @include page-overlay(map-get($zindex, write-review));

  .page-spinner {
    opacity: 0;
    transition: opacity 1s $ease-out-quad;

    &.visible {
      opacity: 1;
    }
  }
}

.write-review-content {
  position: absolute;
  top: 50%;
  left: 50%;
  z-index: map-get($zindex, write-review);
  width: 100%;
  max-width: $max-width;
  max-height: 90%;
  padding: $gutter;
  overflow-y: scroll;
  background-color: $container-background-color;
  opacity: 0;
  transition: opacity 0.5s $ease-out-quad;

  &.visible {
    opacity: 1;
  }
}

.write-review-close {
  position: absolute;
  right: 0;
  padding: $gutter/2;
  line-height: 1;
  color: $body-color-light;
  cursor: pointer;
  opacity: 0;
  transition: all 1s $ease-out-quad;

  &:hover {
    @include hover($body-color-light);
  }

  .visible & {
    opacity: 1;
  }
}

// Review Modal Inner
.review-modal-images {
  @include grid-columns(1 of 2, collapse);
  display: none;
  padding-right: $gutter;

  @include breakpoint(m-up) {
    display: block;
  }
}

.review-modal-form {
  @include grid-columns(1 of 2, collapse);
  padding-left: $gutter;

  .g-recaptcha {
    @include breakpoint(s-only) {
      transform: scale(0.8);
      transform-origin: 0 0;
    }
  }
}

// Left Column (Image / Title)
.review-image-caption {
  margin-top: $gutter/2;
  font-size: 18px;
  color: $body-color-dark;
  text-align: left;
}

// Right Column (Form)
.review-form-heading {
  margin-top: 0;
}

/* scss-combine:components/product/options */
//-- Set Items Only--//
%option-set-item {
  position: relative;
  display: block;
  float: left;
  margin-right: map-get($product-option, space);
  margin-bottom: map-get($product-option, space);
}

//-- Date Fields --//
.form-field-date {

  .form-field-control {
    @extend %clearfix;
  }

  select {
    min-width: inherit;
  }

  .form-select-wrapper {
    display: block;
    float: left;
    width: calc(33.33% - 6px);
    margin-right: 9px;

    &:last-child {
      margin-right: 0;
    }
  }
}

//-- Form Rectangle Options --//
.form-field-rectangle {

  .form-field-control {
    @extend %clearfix;
    display: inline-block;
  }

  .form-input {
    @extend %checkbox-hide;
  }

  .rectangle {
    @extend %option-set-item;
    margin-right: map-get($product-option, space) * 2;
    margin-bottom: map-get($product-option, space) * 2;
  }

  .rectangle-text {
    @extend %border-radius-treatment;
    position: relative;
    z-index: 1;
    display: block;
    height: 44px;
    min-width: 44px;
    padding: 0 map-get($product-option, padding);
    overflow: hidden;
    line-height: 44px;
    text-align: center;
    text-overflow: ellipsis;
    white-space: nowrap;
    cursor: pointer;
    border: 1px solid $form-input-border;
  }

  .rectangle-text:hover {
    color: brightness-adjust($body-color);
  }

  .form-input:focus + .rectangle-text {
    color: map-get($product-option, selected);
  }

  .form-input:checked + .rectangle-text {
    position: relative;
    z-index: 10;
    color: map-get($product-option, selected);
    border-color: map-get($product-option, selected);
  }
}

//-- Swatch Picker --//
.form-field-swatch {

  .swatch-value {
    font-size: 0.8em;
  }

  .form-field-control {
    @extend %clearfix;
    display: inline-block;
  }

  .form-input {
    @extend %checkbox-hide;
  }

  .form-label-text {
    @extend %show-for-sr;
  }

  .swatch-wrap {
    @extend %option-set-item;

    .form-input:checked + .swatch {
      border-color: map-get($product-option, selected);
    }

    &:hover {
      .swatch-pattern-expanded {
        z-index: 5000;
        opacity: 1;
        visibility: visible;
      }
    }
  }

  .swatch {
    @extend %border-radius-treatment;
    position: relative;
    display: block;
    padding: map-get($product-swatch, space);
    cursor: pointer;
    border: 1px solid $form-input-border;

    &:hover {
      border-color: map-get($product-option, hover);
    }
  }

  .swatch-color {
    @extend %border-radius-treatment;
    position: relative;
    display: block;
    width: map-get($product-swatch, size);
    height: map-get($product-swatch, size);
    background: {
      position: 50%;
      repeat: no-repeat;
      size: cover;
    }
  }

  .tertiary,
  .secondary {
    margin-top: map-get($product-option, space);
  }

  .swatch-pattern-expanded {
    position: absolute;
    top: calc(100% + 5px);
    left: calc(100% + 29px);
    display: block;
    width: 108px; // 100px + padding + border
    height: 108px;
    padding: 3px;
    background-color: $body-background-color;
    border: 1px solid map-get($product-option, selected);
    border-radius: 3px;
    opacity: 0;
    visibility: hidden;
    transform: translate(-50%, 0);
    transition: opacity 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);

    @include breakpoint(m-down) { display: none; }
  }

  .swatch-pattern-image {
    display: block;
    width: 100px;
    height: 100px;
    border-radius: 3px;
  }
}

//-- Product Picklist --//
.product-picklist-item {
  position: relative;
  display: block;
}

.product-picklist-radio {
  position: absolute;
  top: 50%;
  margin-top: -7px;
  line-height: 14px;
}

.product-picklist-item-body {
  position: relative;
  display: table;
  width: 100%;
  min-height: map-get($product-pick-list, size) * 1.5;
  padding: map-get($product-pick-list, padding);
  padding-left: (map-get($product-pick-list, size) * 1.5) + map-get($product-pick-list, padding);
  cursor: pointer;
  border: 1px solid transparent;

  &.picklist-none {
    padding-left: (map-get($product-pick-list, size) * 1.5) + map-get($product-pick-list, padding);
  }

  &:hover {
    color: brightness-adjust($body-color);
  }

  [type="radio"]:checked ~ & {
    z-index: 2;
    border-color: map-get($product-option, selected);
  }
}

.product-picklist-image-wrap {
  position: absolute;
  top: map-get($product-pick-list, padding);
  bottom: map-get($product-pick-list, padding);
  left: map-get($product-pick-list, padding);
  width: map-get($product-pick-list, size);
  overflow: hidden;
}

.product-picklist-label-text {
  display: table-cell;
  line-height: 1.4;
  color: inherit;
  vertical-align: middle;
}
.form-file-types,
.form-file-size {
  display: inline-block;
  font-size: 0.7em;
  color: $form-input-color;
  padding: $form-element-padding $form-element-padding 0;

  &:nth-of-type(2n) {
    padding-top: 0;
  }
}

/* scss-combine:components/compare-bar */
.compare-bar {
  position: fixed;
  bottom: -100%;
  z-index: map-get($zindex, compare);
  width: 100%;
  height: auto;
  padding: $gutter * 2 $gutter;
  background-color: $container-background-color;
  border-top: 1px solid $border-color;
  transition: all 1s $ease-out-quad;

  @include breakpoint(s-only) {
    padding: $gutter $gutter / 2;
  }

  &.visible {
    bottom: 0;
    transition: all 0.25s $ease-out-quad;;
  }
}

.compare-bar-internal {
  float: left;

  @include breakpoint(s-only) {
    @include grid-row();
  }
}

.compare-bar-actions {
  float: left;
  height: 75px;

  .compare-bar-clear {
    margin-left: $gutter / 4;
  }

  @include breakpoint(s-only) {
    width: 100%;
    margin-top: $gutter;
    text-align: center;
  }

  @include breakpoint(m-up) {
    transform: translateY(50%);
  }
}

.compare-bar-clear,
.compare-bar-submit {

  @include breakpoint(m-up) {
    transform: translateY(-50%);
  }
}

.product-compare-item {
  position: relative;
  float: left;
  width: 75px;
  padding: 0;
  margin: 0 $gutter 0 0;
  border: 0;

  @include breakpoint(s-only) {
    @include grid-columns(1 of 3);
    margin-right: 0;
  }
}

.product-compare-item-thumbnail {
  @include maintain-ratio();
  display: inline-block;
  vertical-align: middle;
}

.product-compare-item-image {
  @include maintain-ratio();
  background: 50% 50% no-repeat;
  background-size: cover;
}

/* scss-combine:components/compare-buttons */
// Compare Buttons
.compare-item-remove {
  @extend %button-primary;
  position: absolute;
  top: 0;
  right: 0;
  padding: 6px;
  font-size: 12px;
  line-height: 1;
  color: $body-color;
  background: $body-color-light;
  border: 0;
  opacity: 0;
  transition: all 1s $ease-out-quad;

  .compare-grid-item & {
    right: $gutter;
    padding-right: 8px;
    padding-left: 8px;
    font-size: 16px;
  }

  .compare-grid-item &,
  .product-compare-item:hover & {
    opacity: 1;
  }

  &:hover {
    color: $body-color-link;
    @include hover($body-color-light, background-color);
  }
}

/* scss-combine:components/masonry-grid */

.grid-masonry {
  @extend %clearfix;
  margin-right: -$column-gutter;
  margin-left: -$column-gutter;

  @include breakpoint(l, true) {
    margin-right: -($column-gutter * 1.5);
    margin-left: -($column-gutter * 1.5);
  }

  @include breakpoint(xl) {
    margin-right: -($column-gutter * 2);
    margin-left: -($column-gutter * 2);
  }
}

.layout-grid-gutter,
.layout-grid-sizer {
  position: absolute;
  top: 0;
  left: 0;
  height: 0;
  visibility: hidden;
}

.layout-grid-sizer,
.layout-grid-item,
.product-item-empty {
  float: left;
  width: percentage(1 / 2);
  padding: 0 $column-gutter $gutter;
  margin-bottom: $gutter;
  box-sizing: border-box;


  .product-grid-container & {
    margin-bottom: $gutter + $gutter / 2;
  }

  @include breakpoint(l, true) {
    padding-right: $column-gutter * 1.5;
    padding-left: $column-gutter * 1.5;
  }

  @include breakpoint(xl) {
    padding-right: $column-gutter * 2;
    padding-left: $column-gutter * 2;
  }

  .layout-grid-of-3 & {
    @include breakpoint(m-up) {
      width: percentage(1/2);
    }

    @include breakpoint(l-up, true) {
      width: percentage(1/3);
    }
  }

  .layout-grid-of-4 & {
    @include breakpoint(m-up, true) {
      width: percentage(1/3);
    }

    @include breakpoint(l-up) {
      width: percentage(1/4);
    }
  }

  .layout-grid-blog & {
    @include breakpoint(s-only) {
      width: percentage(1);
    }

    @include breakpoint(l-up, true) {
      width: percentage(1/3);
    }

    @include breakpoint(xl-up) {
      width: percentage(1/4);
    }
  }

  &.grid-span-2 {
    width: 100%;

    .layout-grid-of-3 & {
      @include breakpoint(m-up, true) {
        width: percentage(2/3);
      }
    }

    .layout-grid-of-4 & {
      @include breakpoint(m-up, true) {
        width: percentage(2/3);
      }

      @include breakpoint(l-up) {
        width: percentage(1/2);
      }
    }
  }

  &.review-footer,
  &.pagination-container {
    width: 100%;
  }

}

/* scss-combine:components/classic-grid */
.grid-classic {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
}

.collection-wrapper-sorting {
  padding-left: 20px;

  .form-field-title,
  .form-select-wrapper {
    display: inline-block;
  }

  .form-select-wrapper {
    padding-left: 10px;
  }
}

/* scss-combine:components/quick-shop */
.quick-shop-wrapper {
  @include page-overlay(map-get($zindex, overlay));

  .page-spinner {
    opacity: 0;
    transition: opacity 1s $ease-out-quad;

    &.visible {
      opacity: 1;
    }
  }
}

.quick-shop-content {
  position: absolute;
  top: 50%;
  left: 50%;
  z-index: map-get($zindex, quick-shop);
  width: 100%;
  max-width: $max-width;
  max-height: 90%;
  padding: $gutter*2;
  overflow-y: scroll;
  background-color: $container-background-color;
  opacity: 0;
  transition: opacity 1s $ease-out-quad;

  &.visible {
    opacity: 1;
  }
}

.quick-shop-details,
.quick-shop-images {
  @include fill-parent();
  position: relative;

  @include breakpoint(m-up) {
    @include grid-columns(1 of 2, collapse);
  }
}

.quick-shop-details {
  @include breakpoint(m-up) {
    right: 50%;
    padding-right: $gutter;
  }
}

.quick-shop-images {
  @include breakpoint(m-up) {
    left: 50%;
    padding-left: $gutter;
  }
}

.quick-shop-close {
  position: absolute;
  right: 0;
  padding: $gutter/2;
  line-height: 1;
  color: $body-color-light;
  cursor: pointer;
  opacity: 0;
  transition: all 1s $ease-out-quad;

  &:hover {
    @include hover($body-color-light);
  }

  .visible & {
    opacity: 1;
  }
}

.quick-shop-title {
  margin-top: 0;
  margin-bottom: 14px;
}

/* scss-combine:components/errors */
.error-message-modal {
  display: none;
}

.error-message {
  padding-right: $gutter;
  padding-left: $gutter;
  font-size: $body-font-size;
  text-align: center;
}

/* scss-combine:components/amp-product-options */
.amp-product-options {
  .form-field-quantity-control {
    position: relative;
    display: block;

    .form-input {
      width: 100%;
      padding-right: 40px;
      padding-left: 40px;
      text-align: center;
    }

    .quantity-control-action {
      position: absolute;
      top: 50%;
      display: flex;
      justify-content: center;
      align-items: center;
      width: 40px;
      height: 40px;
      margin-top: -20px;
      cursor: pointer;

      &:hover {
        color: $color-primary-dark;
      }
    }

    .quantity-decrement {
      left: 8px;
    }

    .quantity-increment {
      right: 8px;
    }
  }

  .button-progress .complete-text {
    top: calc(50% - 10px);
  }

  .product-alert {
    margin-bottom: 15px;
  }
}


// Navigation
/* scss-combine:components/navigation/navigation-header */

.header-navigation-item {
  text-align: right;

  > a {
    // scss-lint:disable VariableForProperty
    display: inline-block;
    font-family: $heading-font;
    font-size: 16px;
    line-height: 20px;
    text-transform: uppercase;
    transition: color 0.2s $ease-out-quad;

    @include breakpoint(m-up) {
      font-size: 20px;
      line-height: 26px;
    }
  }
}

/* scss-combine:components/navigation/navigation-footer */
.main-footer {

  .nav-menu {
    a {
      text-transform: uppercase;
    }
  }
}

.footer-icons {

  a {
    display: block;
  }

}

/* scss-combine:components/navigation/navigation-slideout */
.navigation-overlay {
  @extend %page-overlay;
  transform: translateX(100%);

  .main-nav-open & {
    display: block;
    opacity: 1;
    visibility: visible;
    transform: translateX(0);
  }
}

.main-content.can-animate,
.main-footer {
  transition: all 0.2s $ease-out-quad;
}

.main-header,
.main-content,
.main-footer {

  .main-nav-closed & {
    transform: translateX(0);

    .lt-ie9 & {
      left: 0;
    }
  }

  .main-nav-open & {
    right: $slide-navigation-regular;
    transition: all 0.2s $ease-out-quad;

    @include breakpoint(m-up) {
      right: $slide-navigation-desktop;
    }

    .lt-ie9 & {
      left: -($slide-navigation-regular);

      @include breakpoint(m-up) {
        left: -($slide-navigation-desktop);
      }
    }
  }
}

.navigation-tier-primary {
  // scss-lint:disable DeclarationOrder
  display: none;

  .alternate-header & {
    display: block;
    width: 100%;
    text-align: right;
  }

  @include breakpoint(l-up) {
    display: block;
  }

  @include breakpoint(m-down) {
    position: absolute;
    top: 100%;
    right: 0;
    left: auto;
    width: auto;
    padding: $gutter;
    margin-left: 0;
    transition: all 0.5s $ease-out-quad;

    &,
    .alternate-header & {
      display: none;
      margin-left: 0;
    }

    &.visible {
      display: block;
    }

    .main-nav-closed & {
      background: $main-nav-background-color-opaque;
    }
  }
}

.navigation-item-primary {
  // scss-lint:disable DeclarationOrder

  .alternate-header & {
    display: inline-block;
    margin-left: $gutter;
  }

  .alternate-header.main-nav-open & {
    display: block;
  }

  @include breakpoint(m-down) {

    &,
    .alternate-header & {
      float: none;
      width: 100%;
      margin-left: 0;
    }
  }
}

.navigation-link-primary {
  color: $main-nav-color;

  &:hover {
    @include hover($main-nav-color);
  }

  @include breakpoint(m-down) {
    display: block;
  }

  .main-nav-open & {
    color: $main-nav-color-light;

    &:hover {
      @include hover($main-nav-color-overlay-contrast);
    }
  }

  &.menu-open {
    color: $main-nav-color-overlay-contrast;

    &:hover {
      @include hover($main-nav-color-overlay-contrast);
    }
  }
}

.navigation-wrap {
  position: fixed;
  top: 0;
  right: -$slide-navigation-regular;
  bottom: 0;
  z-index: map-get($zindex, navigation-wrap);
  width: $slide-navigation-regular;
  height: 100vh;
  padding: ($gutter + $header-height-mobile) $gutter;
  margin: 0;
  overflow: scroll;
  text-align: left;
  background-color: $body-background-color;
  transition: all 0.2s $ease-out-quad;

  @include breakpoint(m-up) {
    right: -$slide-navigation-desktop;
    width: $slide-navigation-desktop;
  }

  @include breakpoint(l-up) {
    padding: $gutter;
  }

  .main-nav-open & {
    right: 0;
  }

  li {
    text-align: left;
  }
}

.navigation-tier-secondary {
  display: none;

  &.visible {
    display: block;
  }
}

.navigation-tier-quaternary,
.navigation-tier-tertiary {
  display: none;
  padding-left: $gutter;

  &.visible {
    display: block;
  }
}

.header-navigation-separation {
  padding-top: $gutter;
  margin-top: $gutter;
  border-top: 1px solid $border-color;
}

/* scss-combine:components/common/pagination */

.pagination-container {
  width: 100%;
  margin-top: $gutter*2.5;

  .review-grid-container & {
    margin-top: 0;
  }
}

.pagination-list {
  @extend %clearfix;
  width: 100%;
}

.pagination-item {
  font-size: 14px;

  &.previous {
    float: left;
    width: 50%;
    padding-right: $gutter/2;
    text-align: left;
  }

  &.next {
    float: right;
    width: 50%;
    padding-left: $gutter/2;
    text-align: right;
  }

  .icon-arrow-right,
  .icon-arrow-left {
    position: absolute;
    top: 50%;
    left: 0;
    display: inline-block;
    margin-top: -7px;
  }

  .icon-arrow-right {
    right: 0;
  }

}

.pagination-link {
  position: relative;
  display: block;

  .next & {
    padding-right: $gutter;
  }

  .previous & {
    padding-left: $gutter;
  }
}

/* scss-combine:components/cart/quick-cart */
.quick-cart-change-currency {
  text-transform: uppercase;
}

.quick-cart-active-currency {
  text-decoration: underline;
}

.quick-cart-currency-selector {
  display: inline-block;
  text-decoration: underline;
  background: $body-background-color;
  border: 0;
  outline: none;
  appearance: none;
}

.quick-cart {

  @include breakpoint(m-up) {
    padding-top: $gutter * 2;
