// Module overrides
/* scss-combine:modules/flickity */
//! Flickity v1.0.0
//http://flickity.metafizzy.co
//---------------------------------------------- */

// scss-lint:disable all

.flickity-enabled { position: relative; }

.flickity-enabled:focus { outline: none; }

.flickity-viewport {
  position: relative;
  height: 100%;
  overflow: hidden;
}

.flickity-slider {
  position: absolute;
  width: 100%;
  height: 100%;
}

.flickity-enabled {
  &.is-draggable {
    //@extend %user-select-none;

    .flickity-viewport {
      //cursor: move;
      //cursor: -webkit-grab;
      cursor: grab;

      &.is-pointer-down {
        //cursor: -webkit-grabbing;
        cursor: grabbing;
      }
    }
  }
}

// ---- previous/next buttons ---- /

.flickity-prev-next-button {
  position: absolute;
  top: 50%;
  width: 44px;
  height: 44px;
  min-width: 0;
  cursor: pointer;
  border: 0;
  border-radius: 50%;
  background: rgba($color-primary-contrast, 0.75);
  transform: translateY(-50%);

  &:disabled {
    cursor: auto;
    opacity: 0.3;
  }

  &:hover {
    background: $color-primary-contrast;
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 5px #09f;
  }

  &:active {
    opacity: 0.6;
  }

  &.previous {
    left: 10px;
    .flickity-rtl & {
      right: 10px;
      left: auto;
    }
  }
  &.next {
    right: 10px;
    .flickity-rtl & {
      right: auto;
      left: 10px;
    }
  }

  svg {
    position: absolute;
    top: 20%;
    left: 20%;
    width: 60%;
    height: 60%;
  }
  .arrow {
    fill: $color-primary-light;
  }
}

// ---- page dots ---- /

.flickity-page-dots {
  position: absolute;
  bottom: -25px;
  width: 100%;
  padding: 0;
  margin: 0;
  line-height: 1;
  text-align: center;
  list-style: none;

  .flickity-rtl & {
    direction: rtl;
  }

  .dot {
    display: inline-block;
    width: 10px;
    height: 10px;
    margin: 0 8px;
    background: $color-primary-light;
    border-radius: 50%;
    cursor: pointer;
    opacity: 0.25;

    &.is-selected {
      opacity: 1;
    }
  }
}

/* scss-combine:modules/baguette-box */
/*!
 * baguetteBox.js
 * @author  feimosi
 * @version 1.4.1
 * @url https://github.com/feimosi/baguetteBox.js
 */

// scss-lint:disable QualifyingElement, IdSelector, SelectorFormat

%arrow-button {
  top: calc(50% - 30px);
  width: 44px;
  height: 60px;
}

#baguetteBox-overlay {
  position: fixed;
  top: 0;
  left: 0;
  z-index: 1000000;
  display: none;
  width: 100%;
  height: 100%;
  overflow: hidden;
  background-color: $modal-background-color;
  opacity: 0;
  transition: opacity 0.5s ease;

  &.visible { opacity: 1; }

  .full-image {
    position: relative;
    display: inline-block;
    width: 100%;
    height: 100%;
    text-align: center;

    figure {
      display: inline;
      height: 100%; // Opera 12 image stretching fix
      margin: 0;
    }

    img {
      display: inline-block;
      width: auto;
      height: auto;
      max-width: 100%;
      max-height: 90%;
      vertical-align: middle;
      box-shadow: 0 0 8px rgba(#000, 0.6);
    }

    figcaption {
      position: absolute;
      bottom: 0;
      display: block;
      width: 100%;
      padding-bottom: 16px;
      font-weight: 300;
      line-height: 1.8;
      color: $container-background-color;
      text-align: center;
      white-space: normal;
    }

    &:before {
      display: inline-block;
      width: 1px;
      height: 50%;
      margin-right: -1px;
      content: "";
    }
  }
}

#baguetteBox-slider {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  white-space: nowrap;
  transition: left 0.4s ease, transform 0.4s ease;

  &.bounce-from-right {
    animation: bounceFromRight 0.4s ease-out;
  }

  &.bounce-from-left {
    animation: bounceFromLeft 0.4s ease-out;
  }
}

@keyframes bounceFromRight{
  0% { margin-left: 0; }
  50% { margin-left: -30px; }
  100% { margin-left: 0; }
}

@keyframes bounceFromLeft{
  0% { margin-left: 0; }
  50% { margin-left: 30px; }
  100% { margin-left: 0; }
}

.baguetteBox-button {
  position: absolute;
  padding: 0;
  margin: 0;
  font-size: 1.6em;
  color: $body-color-light;
  cursor: pointer;
  background-color: transparent;
  border: 0;
  border-radius: 15%;
  outline: none;
  transition: background-color 0.4s ease;

  &:hover { background-color: rgba($container-background-color, 0.1); }

  &#next-button {
    @extend %arrow-button;
    right: 2%;
  }

  &#previous-button {
    @extend %arrow-button;
    left: 2%;
  }

  &#close-button {
    top: 20px;
    right: 20px;
    width: 30px;
    height: 30px;
  }

  // Firefox fix
  svg {
    position: absolute;
    top: 0;
    left: 0;

    g {
      stroke: $body-color-light;
    }
  }
}

// http://tobiasahlin.com/spinkit/
.spinner {
  position: absolute;
  top: 50%;
  left: 50%;
  display: inline-block;
  width: 40px;
  height: 40px;
  margin-top: -20px;
  margin-left: -20px;
}

.double-bounce1,
.double-bounce2 {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: $container-background-color;
  border-radius: 50%;
  opacity: 0.6;
  animation: bounce 2s infinite ease-in-out;
}

.double-bounce2 { animation-delay: -1s; }

@keyframes bounce {
  0%, 100% { transform: scale(0); }

  50% { transform: scale(1); }
}

/* scss-combine:modules/carousel */
.carousel-wrapper {
  height: $carousel-height-mobile;
  padding-bottom: $gutter * 2;
  overflow: hidden;
  background-color: $carousel-background-color;

  &.flickity-enabled {
    height: auto;
  }

  @include breakpoint(l-up) {
    height: $carousel-height-desktop;
  }

  + .layout-module .section-title {
    .carousel-border & {
      border-top: 1px solid $border-color;
    }
  }
}

.carousel-item {
  width: 100%;
}

.carousel-item-background {
  display: block;
  width: 100%;
  height: $carousel-height-mobile;
  background: 50% 50% no-repeat;
  background-size: $carousel-image-crop;

  @include breakpoint(l-up) {
    height: $carousel-height-desktop;
  }
}

.carousel-item-info {
  @extend %wrapper;
  margin-top: $gutter * 2;
  color: $carousel-font-color;

  > *:last-child {
    margin-bottom: 0;
  }

  @include breakpoint(m-up){
    max-width: 50%;
    margin-left: 0;
  }
}

.carousel-item-title {
  margin-top: 0;
  color: $carousel-font-color;
}

.flickity-prev-next-button {
  top: ($carousel-height-mobile/2 + $header-height-mobile/2);
  transition: all 0.5s $ease-out-quad;

  @include breakpoint(l-up) {
    top: ($carousel-height-desktop/2 + $header-height-desktop/2);

    .alternate-header & {
      top: ($carousel-height-desktop/2 + $header-height-mobile/2);
    }
  }

  &:before {
    @extend %icon-base;
    height: 32px;
    font-size: 32px;
    line-height: 32px;
    content: $icon-arrow-left;
  }

  &.next {
    &:before {
      content: $icon-arrow-right;
    }
  }

  &,
  &:active,
  &:hover {
    background: transparent;
    border: 0;
    outline: none;
  }

  svg,
  .arrow {
    display: none;
  }
}

.flickity-page-dots {
  @extend %wrapper;
  top: $carousel-height-mobile + $gutter;
  bottom: auto;
  left: 50%;
  padding-right: $gutter;
  text-align: right;
  transform: translateX(-50%);

  @include breakpoint(l-up) {
    top: $carousel-height-desktop + $gutter;
  }

  .dot {
    display: inline-block;
    width: 16px;
    height: 16px;
    margin: 2px 0 0 $gutter/2;
    line-height: 16px;
    color: $carousel-dot-color;
    content: " ";

    &:before {
      @extend %icon-base;
      content: $icon-circle-full;
    }

    &.is-selected {
      @include hover($carousel-dot-color);
    }
  }
}

/* scss-combine:modules/modals */

.modal-overlay {
  @include page-overlay(map-get($zindex, modal-overlay));
}

.modal-wrapper {
  z-index: map-get($zindex, modal-wrapper);
  width: 100%;
  height: 100vh;
  box-sizing: content-box;
  backface-visibility: hidden;
}

.modal {
  width: 100%;
  max-width: $max-width;
  max-height: calc(100% - #{$gutter * 4});
  padding: 0;
  overflow-y: scroll;

  &.modal-quick-shop {
    opacity: 0;
    transition: opacity 0.5s $ease-out-quad;

    .loaded & {
      opacity: 1;
    }
  }
}

.modal-close {
  position: fixed;
  top: 0;
  right: 0;
  float: none;
  padding: ($gutter / 3) ($gutter / 2);
  color: $color-primary-contrast;

  .modal-close-icon {
    display: inline-block;
    width: $body-font-size;
    height: $body-font-size;
    vertical-align: middle;
  }

  .modal-close-label {
    display: none;
    transition: color 0.4s $ease-out-quad;

    @include breakpoint(m-up) {
      display: inline-block;
      margin-left: 5px;
      line-height: 1;
      vertical-align: middle;
    }
  }

  &:hover {

    &,
    .modal-close-icon,
    .modal-close-label {
      color: $color-primary-contrast;
    }
  }
}

.modal-content {
  @extend %clearfix;
  padding: $gutter;
  background: $body-background-color;

  > *:first-child { margin-top: 0; }
  > *:last-child { margin-bottom: 0; }

  .modal-quick-shop & {
    @include breakpoint(m-up){
      padding: 50px;
    }
  }
}

.modal-toggle {
  padding: 0;
  color: $accent-color;
  background: none;
  border: 0;
  outline: 0;

  &:hover {
    @include hover($accent-color);
  }
}

/* scss-combine:modules/core */

%account-nav {
  padding: 0;
  list-style: none;
  border-width: 0;

  li {
    color: $account-nav-link;
    border: {
      color: $account-border-color;
      style: solid;
      width: 0 1px 1px 1px;
    }

    &:first-child {
      border-top: 1px solid $account-border-color;
      border-radius: $account-border-radius $account-border-radius 0 0;
    }

    &:last-child {
      border-radius: 0 0 $account-border-radius $account-border-radius;
    }

    &.is-active {
      color: $account-nav-link-active;
      border-left: 3px solid $account-accent-color;

      @include breakpoint(m-down) {
        border-right: 2px solid $account-accent-color;
        border-left-width: 2px;
      }
    }
  }

  a {
    display: block;
    padding: 15px;
    color: inherit;
  }
}

%account-layout-nav {
  @include fill-parent();

  @include breakpoint(m-only) {
    @include span-columns(8);
    @include shift(2);
  }

  @include breakpoint(l-up) {
    @include grid-columns(3 of 12, collapse);
  }
}

%account-layout-body {
  @include fill-parent();
  padding-top: $page-padding-top / 2;

  @include breakpoint(m-only) {
    @include span-columns(8);
    @include shift(2);
  }

  @include breakpoint(l-up) {
    @include grid-columns(9 of 12, collapse);
    padding-top: 0;
    padding-left: $gutter * 2;

    .account-nav-hidden & {
      padding-left: 0;
    }
  }
