core/amp/css/product-options:
body {
  padding: 0;
  margin: 0;
}

.alert {
  margin-bottom: 0.75rem;
}

.amp-product-options .form-inline-message {
  margin-top: 0;
  margin-bottom: 1rem;
}

.button-progress {
  position: relative;
}

.button-progress.in-progress .button-text {
  visibility: hidden;
}

.amp-product-options .add-to-cart-quantity-container {
  max-width: 270px;
}

.amp-product-options .site-alerts {
  position: static;
}
