pages/product:
---
product:
  reviews:
    limit: 8
  related_products:
    limit: 8
  videos:
    limit: 12
---

{{#partial "page"}}
{{inject 'showReviews' (lang 'product.reviews.show_reviews')}}
{{inject 'hideReviews' (lang 'product.reviews.hide_reviews')}}

{{> components/common/page-header
  list=breadcrumbs
  page_title=product.title
}}

<div
  class="container product-container"
  data-product-title="{{product.title}}"
  data-product-id={{product.id}}
>
  <div class="layout-module">
    {{#each product.shipping_messages}}
      {{> core/alerts/alert-info message=message}}
    {{/each}}
    <div class="single-product-left">
      {{> components/products/single-details
        this
      }}
    </div>

    <div class="single-product-right">
      {{> components/products/single-image
        this
        class='single-product-images product-images'
      }}
    </div>
  </div>
</div>

{{#if product.videos.list.length}}
  <div class="container products-videos-container">
    <section class="layout-module">
      <h2 class="product-reviews-title section-title">
        {{lang 'product.videos_section' total=product.videos.list.length}}
      </h2>

      <div
        class="
          video-grid-container
          grid-masonry
          {{#if product.videos.list.length '>' 2}}
            layout-grid-of-3
          {{/if}}
        "
      >
        <div class="layout-grid-sizer"></div>
        <div class="layout-grid-gutter"></div>

        {{#each product.videos.list}}
          <div class="layout-grid-item product-video-container">
            <iframe
              src="https://www.youtube.com/embed/{{id}}?rel=0&showinfo=0&autohide=1"
              frameborder="0"
              allowfullscreen
            ></iframe>
          </div>
        {{/each}}
      </div>
    </section>
  </div>
{{/if}}

{{#if settings.show_product_reviews}}
  <div class="container products-reviews-container" id="write_review">
    {{> components/products/reviews
      reviews=product.reviews
      product_title=product.title
    }}
  </div>
{{/if}}

{{#if product.related_products.length}}
  <div class="container products-related-container">
    {{> components/products/related
      grid=product.related_products
      show_quick_shop=theme_settings.show_quick_shop
      default_image=theme_settings.default_image
    }}
  </div>
{{/if}}

{{> core/structured-data/product}}
{{> core/structured-data/product-reviews}}

{{/partial}}

{{> layout/base}}

components/common/page-header:
{{!
  @param page_type - value to test against for conditional displays
  @param list - list of breadcrumbs
  @param description - should only be passed through on categories
}}

<div class="container page-header">
  <header class="layout-module">
    <h1 class="page-title">
      {{#compare page_type 'search' operator='==='}}
        {{#unless page_title}}
            {{lang 'search.label'}}
        {{else}}
          {{lang 'search.results.label' query=page_title}}
        {{/unless}}
      {{else}}
        {{limit page_title 200}}{{#if page_title.length '>' 200}}&hellip;{{/if}}
      {{/compare}}
    </h1>
    {{#compare page_type 'product' operator='==='}}
      {{> components/common/breadcrumbs list=breadcrumbs hide_last='true'}}
    {{else}}
      {{> components/common/breadcrumbs list=breadcrumbs}}
    {{/compare}}
  </header>

  {{#if description}}
  <div class="layout-module collection-description rte">
    {{{category.description}}}
  </div>
  {{/if}}
</div>

components/products/single-details:
{{#if quick_shop}}
  <h2 class="quick-shop-title">{{product.title}}</h2>
{{/if}}

<div class="product-message-area" data-product-cart-message>{{! Dynmaically populated by Alert.js}}</div>

{{#if product.out_of_stock}}
  {{#if product.out_of_stock_message}}
    {{> core/alerts/alert-error message=product.out_of_stock_message}}
  {{else}}
    {{> core/alerts/alert-error message=(lang 'product.sold_out')}}
  {{/if}}
{{/if}}

{{#if product.release_date}}
  {{> core/alerts/alert-info message=product.release_date}}
{{/if}}

{{#if settings.show_product_rating}}
  <div class="product-rating-block">
    <a
      class="single-product-rating"
      {{#unless quick_shop}}
        {{#if settings.show_product_reviews}}
          href="#write_review"
          data-scroll-to="#write_review"
        {{/if}}
      {{/unless}}
    >
      {{> components/products/ratings
        rating=this.product.rating
        total_reviews=this.product.num_reviews
        show_reviews=settings.show_product_reviews
      }}
    </a>
  </div>
{{/if}}

<div class="product-price" data-product-price>
  {{> core/products/price
    price=product.price
  }}
</div>

{{#if product.call_for_price}}
  <p class="product-call-for-price">{{product.call_for_price}}</p>
{{/if}}

{{#if product.description}}
  <div class="product-description-container rte">
    {{{product.description}}}
  </div>
{{/if}}

{{{snippet 'product_description'}}}

{{#unless quick_shop}}
  <div class="single-product-details-block">
    {{> components/products/additional-info}}
  </div>
{{/unless}}

<div data-variation-preview></div>

<div class="product-option-message-area" data-product-option-message>
  {{! Dynamically populated by Alert.js}}
</div>

<form
  method="post"
  action="{{product.cart_url}}"
  enctype="multipart/form-data"
  data-cart-item-add
  data-product-option-change
>
  <input type="hidden" name="action" value="add">
  <input type="hidden" name="product_id" value="{{product.id}}"/>

  {{#if product.options.length}}
    <div class="product-options">
      {{#each product.options}}
        {{{dynamicComponent 'core/forms/options'}}}
      {{/each}}
    </div>
  {{/if}}

  {{#if product.customizations.length}}
    <div class="product-customizations">
      {{#each product.customizations}}
        {{{dynamicComponent 'core/forms/customizations'}}}
      {{/each}}
    </div>
  {{/if}}

  {{#if product.event_date}}
    {{> core/products/event}}
  {{/if}}

  {{#if product.bulk_discount_rates.length}}
    <div class="single-product-details-block">
      <h2 class="product-details-heading">
        {{lang 'product.bulk_pricing.title'}}
      </h2>
      <ul>
        {{#each product.bulk_discount_rates}}
          <li>
            {{lang 'product.bulk_pricing.range' min=min max=max}}
            {{#if type '===' 'percent'}}
              {{lang 'product.bulk_pricing.percent' discount=discount.formatted}}
            {{/if}}
            {{#if type '===' 'fixed'}}
              {{lang 'product.bulk_pricing.fixed' discount=discount.formatted}}
            {{/if}}
            {{#if type '===' 'price'}}
              {{lang 'product.bulk_pricing.price' discount=discount.formatted}}
            {{/if}}
          </li>
        {{/each}}
      </ul>
    </div>
  {{/if}}

  {{#if product.show_quantity_input}}
    <div class="form-field product-form-quantity">
      <label class="form-label">
        <span class="form-label-text">
          {{lang 'product.quantity'}}
        </span>
        {{> components/common/quantity
          min_purchase_quantity=product.min_purchase_quantity
          max_purchase_quantity=product.max_purchase_quantity
          value=1
        }}
      </label>
    </div>
  {{else}}
    <input type="hidden" name="qty[]" value="1" />
  {{/if}}

  {{#if product.can_purchase}}
    <div class="product-add-to-cart" data-product-add>
      <button
        type="submit"
        class="button button-primary button-wide-medium add-to-cart button-progress"
        data-button-purchase
        data-default-text="{{lang 'product.add_to_cart'}}"
        data-progress-text="{{lang 'product.add_to_cart_progress'}}">
        <span class="button-text">
          {{#if product.pre_order}}
            {{lang 'product.pre_order'}}
          {{else}}
            {{lang 'product.add_to_cart'}}
          {{/if}}
        </span>
      </button>
    </div>
  {{else}}
    {{#if product.price}}
      {{#if product.out_of_stock}}
        <div class="button button-primary button-wide-medium add-to-cart button-disabled">
          {{lang 'product.add_to_cart'}}
        </div>
      {{/if}}
    {{/if}}
  {{/if}}

  {{{snippet 'product_details'}}}
</form>

{{#if settings.show_wishlist}}
  <form
    method="post"
    class="single-product-wishlist-form"
    action="{{product.add_to_wishlist_url}}"
    data-wishlist-add
  >
    <input
      type="submit"
      class="button button-secondary button-wide-medium add-to-wishlist"
      value="{{lang 'product.add_to_wishlist'}}"
    >
  </form>
{{/if}}

core/structured-data/product:
{{!-- Product: include in product.html page template --}}

<script id="schema-product">

  var schema = document.createElement('script');
  schema.type = 'application/ld+json';
  schema.text = JSON.stringify({
    "@context": "http://schema.org/",
    "@type": "Product",
    "name": "{{product.title}}",
    "image": "{{getImage product.main_image}}",
    "description": "{{json product.description}}",
    "sku": "{{product.sku}}",
    "brand": {
      "@type": "Thing",
      "name": "{{product.brand.name}}"
    },
    {{#if product.reviews.list}}
      "aggregateRating": {
        "@type": "AggregateRating",
        "ratingValue": "{{product.rating}}",
        "reviewCount": "{{product.num_reviews}}"
      },
    {{/if}}
    "offers": {
      "@type": "Offer",
      "priceCurrency": "{{currency_selector.active_currency_code}}",
      "price": "{{#if product.price.without_tax}}{{product.price.without_tax.value}}{{/if}}{{#if product.price.with_tax}}{{#unless product.price.without_tax}}{{product.price.with_tax.value}}{{/unless}}{{/if}}",
      "itemCondition": "{{#if product.condition '===' 'New'}}http://schema.org/NewCondition{{/if}}{{#if product.condition '===' 'Used'}}http://schema.org/UsedCondition{{/if}}{{#if product.condition '===' 'Refurbished'}}http://schema.org/RefurbishedCondition{{/if}}",
      "availability": "{{#unless product.out_of_stock_message}}http://schema.org/InStock{{else}}http://schema.org/OutOfStock{{/unless}}",
      "seller": {
        "@type": "Organization",
        "name": "{{settings.store_name}}"
      }
    }
  });

  document.querySelector('body').appendChild(schema);

  var script = document.getElementById('schema-product');
  script.parentElement.removeChild(script);
</script>

core/structured-data/product-reviews:
{{!-- Product Reviews: include in product.html page template --}}

{{#if settings.show_product_reviews}}
  {{#each product.reviews.list}}
    <script type="application/ld+json">
      {
        "@context": "http://schema.org/",
        "@type": "Review",
        "itemReviewed": {
          "@type": "Product",
          "image": "{{getImage ../product.main_image}}",
          "name": "{{../product.title}}"
        },
        "reviewRating": {
          "@type": "Rating",
          "ratingValue": "{{rating}}"
        },
        "name": "{{title}}",
        "author": {
          "@type": "Person",
          "name": "{{name}}"
        },
        "reviewBody": "{{text}}",
        "publisher": {
          "@type": "Organization",
          "name": "{{../settings.store_name}}"
        }
      }
    </script>
  {{/each}}
{{/if}}

